INSERT INTO `quest_template_locale` VALUES (31368, 'frFR', 'Les Chroniqueurs', 'Parlez au Chroniqueur Cho au Siège de la Connaissance dans le Val de l’Éternel printemps.', 'Ainsi donc, l’histoire de cette contrée vous intéresse ?$B$BTrès bien ! Vous devriez en parler au chroniqueur Cho, au siège de la Connaissance.$B$BVous le trouverez dans la salle au-dessus de l’entrée du palais Mogu’shan. Parlez à Mishi pour vous rendre sur place.', '', '', '', '', '', '', 0);
