INSERT INTO `quest_template_locale` VALUES (31382, 'deDE', 'Die Verteidigung des Niuzaotempels', 'Sprecht mit Ogo dem Ältesten im Niuzaotempel in der Tonlongsteppe.', 'Der Niuzaotempel im äußersten Westen, wird von den Mantis angegriffen.$B$BBis jetzt konnte der Große Himmlische sie aufhalten, aber auf sich allein gestellt kann selbst er nicht ewig durchhalten.$B$BIhr müsst den Verteidigern helfen, $C. Der Tempel darf nicht fallen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31382, 'esES', 'Defensa del Templo de Niuzao', 'Habla con Ogo el Viejo en el Templo de Niuzao, en las Estepas de Tong Long.', 'El Templo de Niuzao, situado en la lejanía, al oeste, está siendo atacado por los mántides.$B$BEl gran Celestial está conteniéndolos, pero no resistirá solo mucho más tiempo.$B$BVe y ayuda a los defensores, $c. ¡No podemos permitir que el templo caiga!', '', '', '', '', '', '', 0);
