INSERT INTO `quest_template_locale` VALUES (31447, 'frFR', 'Une fin à la souffrance', 'Tuez le Sombre Maître Gandling.', 'La seule chose qui pourra lever la malédiction qui règne sur cet endroit est la mort du sombre maître Gandling.$b$bTuez-le, $p. Et qu\'il souffre !', '', 'Tuez le Sombre Maître pour lever la malédiction.', 'Sombre Maître Gandling', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31447, 'itIT', 'La fine della sofferenza', 'Uccidi il Maestro Oscuro Gandling.', 'La chiave per rimuovere la maledizione presente in questo luogo risiede nella morte del Maestro Oscuro Gandling.$B$BFallo, $p, e che la sua sia una morte piena di sofferenza!', '', 'Uccidi il Maestro Oscuro per rimuovere la maledizione.', 'Maestro Oscuro Gandling', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31447, 'koKR', '고통의 끝', '암흑스승 간들링을 없애야 합니다.', '이 장소에 내려진 저주는 암흑스승 간들링의 완전한 죽음으로만 풀릴 수 있다.$B$B가라, $p. 놈이 고통받게 해 다오!', '', '저주를 풀려면 간들링을 없애야 한다.', '암흑스승 간들링', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31447, 'ptBR', 'Um fim para o sofrimento', 'Mate o Umbromestre Gandling.', 'A chave para quebrar a maldição deste lugar jaz na morte final do Umbromestre Gandling.$B$BMate-o em definitivo e faça-o sofrer!', '', 'Mate o Umbromestre para quebrar a maldição.', 'Umbromestre Gandling', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31447, 'ruRU', 'Конец страданиям', 'Убейте темного магистра Гандлинга.', 'Это место так и будет проклято, пока жив темный магистр Гандлинг.$B$BУбей его, $n. И пусть смерть его будет мучительна.', '', 'Убейте темного магистра, чтобы снять проклятие.', 'Темный магистр Гандлинг', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31447, 'zhCN', '苦难的终结', '杀死黑暗院长加丁。', '要解除这个地方的诅咒，关键在于杀死黑暗院长加丁。$B$B杀掉他，$p，让他也尝一尝受苦的滋味！', '', '杀死黑暗院长，解除诅咒。', '黑暗院长加丁', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31447, 'zhTW', '苦難的終點', '殺死黑暗院長加丁。', '黑暗院長加丁的真正死亡是解除這地方詛咒的關鍵。$B$B上吧，$p，讓他痛苦!', '', '殺死黑暗院長以解除詛咒。', '黑暗院長加丁', '', '', '', 0);
