INSERT INTO `quest_template_locale` VALUES (31516, 'frFR', 'Poussière tu redeviendras', 'Plantez les Lames de l\'oint dans le cadavre de la Grande inquisitrice Blanchetête pour lui donner le repos éternel.', '<Vous saisissez la deuxième lame et sentez le pouvoir des épées courir en vous.>$B$B<Il ne reste qu’une chose à faire. Comme la mystérieuse femme encapuchonnée vous l’a expliqué, pour mettre fin à la croisade, vous devez planter les deux épées dans le cadavre de la grande inquisitrice Blanchetête.>', '', 'Plongez les lames de l’oint dans son cadavre pour mettre fin à la tyrannie de la Croisade écarlate.', 'Grande inquisitrice Blanchetête', '', '', '', 0);
