INSERT INTO `quest_template_locale` VALUES (31525, 'frFR', 'Un défi de taille : le chef d’escadre Ner’onok', 'Terminez le Siège du temple de Niuzao en mode Défi.', '$n, il faut que vous rassembliez un groupe de gens au regard d’aigle et que vous vous rendiez au siège du temple de Niuzao. Une fois là-bas, activez l’orbe de défi et tuez Ie chef d’escadre Ner’onok.$B$BRevenez me voir quand vous aurez réussi ce défi, et je vous récompenserai généreusement.', '', '', '', '', '', 'Retournez voir le Maître des défis Wuli au Sanctuaire des Deux-Lunes, dans le Val de l’Éternel printemps.', 0);
INSERT INTO `quest_template_locale` VALUES (31525, 'itIT', 'Una degna sfida: Caposciame Ner\'onok', 'Completa l\'Assedio al Tempio di Niuzao in modalità Sfida.', '$n, ho bisogno di allestire un gruppo dall\'occhio vigile da inviare nell\'Assedio al Tempio di Niuzao. Attivate il Globo dello Sfidante che trovate all\'interno e uccidete il Caposciame Ner\'onok.$B$BTornate da me quando avrete portato a termine questa sfida e vi ricompenserò adeguatamente.', '', '', '', '', '', 'Torna dallo Sfidante Wuli al Santuario delle Due Lune nella Vallata dell\'Eterna Primavera.', 0);
INSERT INTO `quest_template_locale` VALUES (31525, 'ptBR', 'Um desafio digno: líder aéreo Ner\'onok', 'Completar o Modo Desafio do Cerco ao Templo Niuzao.', '$n, eu preciso que você reúna um grupo de olho vivo e os lidere no Cerco ao Templo Niuzao. Ative o Orbe do Desafiante e derrube o líder aéreo Nero\'nok.$B$BFale novamente comigo quando completar esse desafio, e eu $Go:a; recompensarei magnificamente.', '', '', '', '', '', 'Fale novamente com o Desafiante Wuli no Santuário das Duas Luas, que fica no Vale das Flores Eternas.', 0);
INSERT INTO `quest_template_locale` VALUES (31525, 'ruRU', 'Серьезное испытание: командующий флангом Нер\'онок', 'Пройдите Осаду храма Нюцзао в режиме испытания.', 'Я хочу, чтобы ты $gсобрал:собрала; зоркую группу и $gпринял:приняла; участие в осаде храма Нюцзао. Когда прибудешь на место, задействуй сферу испытания и сровняй с землей командующего флангом Нер\'онока.$B$BВозвращайся ко мне за наградой, когда завершишь испытание.', '', '', '', '', '', 'Вернитесь к мастеру испытаний Ули в Святилище Двух Лун, что в Вечноцветущем доле.', 0);
INSERT INTO `quest_template_locale` VALUES (31526, 'esES', 'Un desafío digno: Durand', 'Completa el Monasterio Escarlata en modo desafío.', '$n, necesito que reúnas un grupo fuerte y lo lleves al Monasterio Escarlata. Activa el orbe de contendiente que hay dentro y machaca a Durand con toda tu furia.$B$BVuelve aquí cuando hayas completado este desafío y te recompensaré generosamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31526, 'frFR', 'Un défi de taille : Durand', 'Terminez le Monastère Écarlate en mode Défi.', '$n, il faut que vous rassembliez un groupe résolu et que vous vous rendiez au monastère Écarlate. Une fois là-bas, activez l\'orbe de défi et trouvez et châtiez Durand d’une fureur juste.$B$BRevenez me voir quand vous aurez accompli ce défi et je vous récompenserai généreusement.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31526, 'itIT', 'Una degna sfida: Durand', 'Completa il Monastero Scarlatto in modalità Sfida.', '$n, ho bisogno di allestire un gruppo solido da inviare al Monastero Scarlatto. Attivate il Globo dello Sfidante che trovate all\'interno e uccidete Durant con tutta la furia possibile.$B$BTornate da me quando avrete portato a termine questa sfida e vi ricompenserò adeguatamente.', '', '', '', '', '', '', 0);
