INSERT INTO `quest_template_locale` VALUES (31527, 'deDE', 'Eine würdige Herausforderung: Flammenwirker Koegler', 'Schließt die Scharlachroten Hallen im Herausforderungsmodus ab.', '$n, Ihr müsst eine gewitzte Gruppe zusammenstellen und sie in die Tiefen der Scharlachroten Hallen führen. Aktiviert die Kugel des Herausforderers und setzt dem bücherverbrennenden Treiben von Flammenwirker Koegler ein Ende.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31527, 'esES', 'Un desafío digno: tejedor de fuego Koegler', 'Completa las Cámaras Escarlata en modo desafío.', '$n, necesito que reúnas un grupo avezado y te adentres en las profundidades de las Cámaras Escarlata. Activa el orbe de contendiente que hay allí y pon fin a la quema de libros del tejedor de fuego Koegler.', '', '', '', '', '', '', 0);
