INSERT INTO `quest_template_locale` VALUES (31527, 'frFR', 'Un défi de taille : le tisseur de flammes Koegler', 'Terminez les Salles Écarlates en mode Défi.', '$n, il faut que vous rassembliez un groupe plein d’adresse et que vous vous rendiez dans les profondeurs des salles Écarlates. Une fois là-bas, activez l’orbe de défi et tuez le tisseur de flammes Koegler pour l’empêcher de brûler davantage de livres.', '', '', '', '', '', '', 0);
