INSERT INTO `quest_template_locale` VALUES (31551, 'zhTW', '去抓一隻來！', '抓一隻戰寵。', '目前為止你都做得很好，$n！你自己都還沒發覺，你已經要變成一個寵物專家了。現在你該去為自己抓一隻專屬的寵物！$b$b回去找一隻戰寵，與牠交戰，並在牠生命力上限偏低時把你的陷阱丟出去。完成之後，把你抓到的東西帶回來給我看看。$B$B記住，你至少要有一隻戰寵達到等級3才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'deDE', 'Erste Erfahrungen', 'Gewinnt einen Kampf mit einem Haustier.', 'Nachdem Ihr jetzt zum Ausbilden von Tieren qualifiziert seid, ist es Zeit, Eure Fähigkeiten unter Beweis zu stellen!$b$bZieht in den Wald und stellt Euch einem Kampfhaustier. Wenn Ihr gewonnen habt, kommt wieder zu mir, dann setze ich Eure Ausbildung fort. Viel Glück!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'esES', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal al bosque y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'esMX', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal al bosque y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'frFR', 'Apprendre les ficelles', 'Gagnez un combat de mascottes.', 'Maintenant que vous savez dresser les familiers, il est temps de mettre votre talent à l\'épreuve !$b$bAllez dans la forêt et attaquez une mascotte de combat. Revenez me voir quand vous aurez gagné, et nous poursuivrons les leçons. Bonne chance !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'itIT', 'Imparare le regole', 'Vinci uno scontro tra mascotte.', 'Ora che sei $gun allenatore:un\'allenatrice; di mascotte, è giunta l\'ora di mettere alla prova le tue abilità!$b$bVai là fuori e affronta una mascotte da combattimento. Torna da me dopo che avrai vinto e continueremo l\'addestramento. Buona fortuna!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'koKR', '요령 익히기', '애완동물 대전에서 승리해야 합니다.', '애완동물 조련사가 되었으니 이제 솜씨를 시험해볼 차례군요!$b$b숲으로 가서 아무 전투 애완동물이나 골라 싸움을 거세요. 승리하고 난 뒤 제게 다시 돌아오시면 훈련을 계속할 거예요. 행운을 빌어요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'ptBR', 'Pegando o jeito', 'Vencer uma batalha de mascotes.', 'Agora que você sabe adestrar mascotes, é hora de testar suas habilidades!$B$BSiga pela floresta e enfrente uma mascote de batalha. Volte quando tiver vencido, e eu continuarei seu treinamento. Boa sorte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'ruRU', 'Изучение основ', 'Победите в битве питомцев.', 'Теперь, когда ты умеешь обучать питомцев, пора бы проверить навыки!$b$bОтправляйся в лес и вступи там в схватку с боевым питомцем. Когда одержишь победу, возвращайся ко мне, и я продолжу обучать тебя. Удачи!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'zhCN', '驯服宠物', '赢得一场宠物战斗。', '既然你是一名宠物训练师，那么考验你技术的时候到了！$b$b前往森林并挑选一只战斗宠物进行决斗。赢了之后回来找我，我会继续你的训练。祝你好运！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31552, 'zhTW', '基礎訓練', '在一場寵物對戰中獲勝。', '現在你已經是個寵物訓練師，是時候測試一下你的技能了！$b$b到森林去挑選一隻戰寵，並與其交戰。獲勝之後就回來找我，我會繼續訓練你。祝你好運！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'deDE', 'Auf dem Wege der Besserung', 'Heilt Eure Haustiere vollständig, indem Ihr Seriadne die Stallmeisterin aufsucht.', 'Nachdem Ihr nun einen Kampf gewonnen habt, müssen wir Eure Haustiere heilen, damit sie wieder einsatzbereit sind! Sucht unsere Stallmeisterin Seriadne auf, um Eure Haustiere zu heilen und wiederzubeleben.', '', 'Seriadne, die hiesige Stallmeisterin, kann Eure Tiere heilen und wiederbeleben.', 'Seriadne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'esES', 'Mejorando', 'Sana tus mascotas por completo visitando a Seriadne, la maestra de establos.', 'Ahora que ya has ganado tu primer duelo, necesitamos sanar a tus mascotas para que puedan seguir luchando. Visita a Seriadne, nuestra maestra de establos local, para sanar y revivir a tus mascotas.', '', 'Seriadne, la maestra de establos local, puede sanar y revivir a tus mascotas.', 'Seriadne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'esMX', 'Mejorando', 'Sana tus mascotas por completo visitando a Seriadne, la maestra de establos.', 'Ahora que ya has ganado tu primer duelo, necesitamos sanar a tus mascotas para que puedan seguir luchando. Visita a Seriadne, nuestra maestra de establos local, para sanar y revivir a tus mascotas.', '', 'Seriadne, la maestra de establos local, puede sanar y revivir a tus mascotas.', 'Seriadne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'frFR', 'Panser ses plaies', 'Soignez vos mascottes en allant voir Seriadne, le Maître des écuries.', 'Maintenant que vous avez remporté un combat, il faut soigner vos mascottes pour qu\'elles puissent se battre à nouveau ! Allez voir Seriadne, notre maître des écuries, pour soigner et ranimer vos compagnons.', '', 'Seriadne, le maître des écuries des lieux, peut soigner et ranimer vos mascottes.', 'Seriadne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'itIT', 'Cure intensive', 'Guarisci le ferite delle tue mascotte facendo visita alla stalliera Seriadne.', 'Ora che hai vinto uno scontro, dobbiamo curare le tue mascotte per farle combattere ancora! Vai da Seriadne, la nostra stalliera, per curare o far resuscitare le tue mascotte.', '', 'Seriadne, la stalliera locale, può curare e resuscitare le tue mascotte.', 'Seriadne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'koKR', '채찍 다음엔 당근', '야수 관리인 세리아드네를 찾아가 애완동물을 완전히 치유해야 합니다.', '애완동물이 싸움에 이겼으니 다음에도 싸울 수 있도록 치유해줘야 해요! 이 지역의 야수 관리인인 세리아드네를 찾아가서 애완동물을 회복시키거나 되살려주세요.', '', '야수 관리인 세리아드네는 애완동물을 치유하고 되살릴 수 있습니다.', '세리아드네', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'ptBR', 'Cuidados médicos', 'Fale com Seriadne, a Mestre de Estábulo, para curar suas mascotes de batalha completamente.', 'Agora que você venceu uma batalha, precisamos curar suas mascotes para que você possa lutar outro dia! Visite Seriadne, nossa mestre de estábulo, para curar e ressuscitar suas mascotes.', '', 'Seriadne, a Mestre de Estábulo, pode curar e reviver suas mascotes.', 'Seriadne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'ruRU', 'На поправку', 'Излечите своих питомцев полностью, поговорив со смотрительницей стойл Сериадной.', 'Теперь, когда бой выигран, надо подлечить питомцев, чтобы они могли сражаться и дальше! Следует обратиться к смотрителю стойл Сериадне, чтобы вылечить и оживить питомцев.', '', 'Сериадна, местная смотрительница стойл, может исцелять и оживлять ваших питомцев.', 'Сериадна', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'zhCN', '治疗宠物', '拜访兽栏管理员瑟里亚迪，治疗你的宠物。', '既然你已经赢得了一场战斗，我们就需要治疗你的宠物，这样它们才能继续战斗！拜访一下我们这里的兽栏管理员瑟里亚迪，来治疗和复活你的宠物。', '', '这里的兽栏管理员瑟里亚迪可以治愈和复活你的宠物。', '瑟里亚迪', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31553, 'zhTW', '康復', '去拜訪獸欄管理員瑟里亞迪，以治療你的寵物並恢復其生命力上限。', '現在你在戰鬥中獲勝了，我們必須治癒你的寵物，好讓牠們還有能力再上戰場！去找我們當地的獸欄管理員瑟里亞迪來治療和復活你的寵物。', '', '當地的獸欄管理員瑟里亞迪能夠治癒和復活你的寵物。', '瑟里亞迪', '', '', '', 0);
