INSERT INTO `quest_template_locale` VALUES (31583, 'deDE', 'Auf dem Wege der Besserung', 'Heilt Eure Haustiere vollständig, indem Ihr Jaelysia die Stallmeisterin aufsucht.', 'Nachdem Ihr nun einen Kampf gewonnen habt, müssen wir Eure Haustiere heilen, damit sie wieder einsatzbereit sind! Sucht unsere Stallmeisterin Jaelysia auf, um Eure Haustiere zu heilen und wiederzubeleben.', '', 'Jaelysia, die hiesige Stallmeisterin, kann Eure Haustiere heilen und wiederbeleben.', 'Jaelysia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'esES', 'Mejorando', 'Sana a tus mascotas hasta que estén completamente restablecidas donde Esbina, la maestra de establos.', 'Ahora que has ganado la batalla, tenemos que sanar a tus mascotas para que puedan luchar otro día. Visita a Jaelysia, nuestra maestra de establos local para sanar y revivir a tus mascotas.', '', 'Jaelysia, la maestra de establos local puede sanar y revivir a tus mascotas.', 'Jaelysia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'esMX', 'Mejorando', 'Sana a tus mascotas hasta que estén completamente restablecidas donde Esbina, la maestra de establos.', 'Ahora que has ganado la batalla, tenemos que sanar a tus mascotas para que puedan luchar otro día. Visita a Jaelysia, nuestra maestra de establos local para sanar y revivir a tus mascotas.', '', 'Jaelysia, la maestra de establos local puede sanar y revivir a tus mascotas.', 'Jaelysia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'frFR', 'Panser ses plaies', 'Soignez vos mascottes en allant voir Jaelysia, le Maître des écuries.', 'Maintenant que vous avez remporté un combat, il faut soigner vos mascottes pour qu\'elles puissent se battre à nouveau ! Allez voir Jaelysia, notre maître des écuries, pour soigner et ranimer vos compagnons.', '', 'Jaelysia, le maître des écuries des lieux, peut soigner et ranimer vos mascottes.', 'Jaelysia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'itIT', 'Cure intensive', 'Guarisci le ferite delle tue mascotte facendo visita alla stalliera Jaelysia.', 'Ora che hai vinto uno scontro, dobbiamo curare le tue mascotte per farle combattere ancora! Vai da Jaelysia, la nostra stalliera, per curare o far resuscitare le tue mascotte.', '', 'Jaelysia, la stalliera locale, può curare e resuscitare le tue mascotte.', 'Jaelysia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'koKR', '채찍 다음엔 당근', '야수 관리인 자엘리시아를 찾아가 애완동물을 완전히 치유해야 합니다.', '애완동물이 싸움에 이겼으니 다음에도 싸울 수 있도록 치유해줘야 한다네! 이 지역의 야수 관리인인 자엘리시아를 찾아가서 애완동물을 회복시키거나 되살려주게나.', '', '야수 관리인 자엘리시아는 애완동물을 치유하고 되살릴 수 있습니다.', '자엘리시아', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'ptBR', 'Cuidados médicos', 'Fale com Jaelysia, a Mestre de Estábulo, para curar suas mascotes de batalha completamente.', 'Agora que você venceu uma batalha, precisamos curar suas mascotes para que você possa lutar outro dia! Visite Jaelysia, nossa mestre de estábulo, para curar e ressuscitar suas mascotes.', '', 'Jaelysia, a Mestre de Estábulo, pode curar e reviver suas mascotes.', 'Jaelysia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'ruRU', 'На поправку', 'Излечите своих питомцев полностью, поговорив со смотрительницей стойл Джелисией.', 'Теперь, когда бой выигран, надо подлечить питомцев, чтобы они могли сражаться и дальше! Обратись к смотрительнице стойл Джелисии, чтобы вылечить или оживить питомцев.', '', 'Джелисия, местная смотрительница стойл, может исцелять и оживлять ваших питомцев.', 'Джелисия', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'zhCN', '治疗宠物', '拜访兽栏管理员贾琳希亚，治疗你的宠物。', '既然你已经赢得了一场战斗，我们就需要治疗你的宠物，这样它们才能继续战斗！拜访一下我们这里的兽栏管理员贾琳希亚，来治疗和复活你的宠物。', '', '这里的兽栏管理员贾琳希亚可以治愈和复活你的宠物。', '贾琳希亚', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31583, 'zhTW', '康復', '去拜訪獸欄管理員賈琳希亞，以治療你的寵物並恢復其生命力上限。', '現在你在戰鬥中獲勝了，我們必須治癒你的寵物，好讓牠們還有能力再上戰場！去找我們本地的獸欄管理員賈琳希亞來治療和復活你的寵物。', '', '當地的獸欄管理員賈琳希亞可以治癒和復活你的寵物。', '賈琳希亞', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'deDE', 'Hab\' eins!', 'Fangt ein Kampfhaustier.', 'So weit, so gut, $n! Wenn Ihr so weitermacht, macht Euch bei dem ganzen Haustierkram bald keiner mehr was vor. Jetzt solltet Ihr auch mal ein eigenes Haustier fangen!$b$bZieht wieder los, sucht Euch ein Kampfhaustier, gegen das Ihr kämpfen könnt, greift es an und werft Eure Falle, sobald seine Gesundheit niedrig genug ist. Dann bringt mir Euren Fang zur Begutachtung.$B$BUnd immer dran denken: Ihr braucht mindestens ein Haustier der Stufe 3, um Eure Falle verwenden zu können!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'esES', '¡Tengo una!', 'Caza una mascota de duelo.', 'Buen trabajo de momento, $n. Serás $gun:una; profesional de esto de las mascotas antes de que te des cuenta. ¡Es hora de capturar tu propia mascota!$b$bSal de nuevo y encuentra una mascota con la que luchar, enfréntate en duelo con ella y lanza tu trampa cuando su salud esté baja. Cuando termines, tráeme a la mascota que hayas atrapado para que yo la vea.$b$bRecuerda, ¡para utilizar la trampa te hace falta al menos una mascota de nivel 3!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'esMX', '¡Tengo una!', 'Caza una mascota de duelo.', 'Buen trabajo de momento, $n. Serás $gun:una; profesional de esto de las mascotas antes de que te des cuenta. ¡Es hora de capturar tu propia mascota!$b$bSal de nuevo y encuentra una mascota con la que luchar, enfréntate en duelo con ella y lanza tu trampa cuando su salud esté baja. Cuando termines, tráeme a la mascota que hayas atrapado para que yo la vea.$b$bRecuerda, ¡para utilizar la trampa te hace falta al menos una mascota de nivel 3!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'frFR', 'J’en ai une !', 'Attrapez une mascotte de combat.', 'Vous faites du bon travail pour l\'instant, $n. Vous serez $gun:une; pro du dressage en moins de temps qu\'il n\'en faut pour le dire. Et maintenant, il est l\'heure d\'attraper votre propre compagnon !$b$bTrouvez une mascotte de combat et attaquez-la, et quand elle sera affaiblie, lancez votre piège. Rapportez-moi votre prise quand vous en aurez une.$b$bN\'oubliez pas qu\'il faut avoir au moins une mascotte de niveau 3 pour pouvoir utiliser le piège !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'itIT', 'Presa!', 'Cattura una Mascotte da Combattimento.', 'Ottimo lavoro finora, $n. Diventerai $gun:una; professionista in men che non si dica. Ora è arrivato il momento di catturare una mascotte!$b$bTorna fuori e trova una Mascotte da Combattimento, affrontala e poi lancia la tua trappola quando l\'avrai indebolita a dovere. Porta la tua preda da me quando avrai finito.$b$bRicorda, devi possedere almeno una mascotte di livello 3 per usare la trappola!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'koKR', '잡았다!', '전투 애완동물을 잡아야 합니다.', '$n, 지금까지 아주 잘했네! 눈 깜짝할 새에 애완동물 다루는 데 전문가가 됐으니까. 이제 자네의 애완동물을 직접 잡아볼 차례군!$b$b다시 나가서 다른 전투 애완동물을 찾아 싸움을 건 다음, 놈의 생명력이 낮아졌을 때 덫을 던지게. 애완동물을 잡으면 내게 돌아와서 보여주고.$b$b덫을 쓰려는 애완동물이 최소 3 레벨은 돼야 한다는 걸 기억하게나!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'ptBR', 'Peguei uma!', 'Capture uma mascote de batalha.', 'Você trabalhou bem até agora, $n. Vai ficar profissional nesse negócio de mascotes rapidinho. Agora é hora de pegar uma mascote para você.$B$BVolte e procure uma mascote de batalha para enfrentar. Jogue sua armadilha quando ela ficar com poucos pontos de vida. Traga-a para mim quando tiver conseguido pegá-la.$b$bLembre-se, você precisa ter ao menos uma mascote em nível 3 para usar a armadilha!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'ruRU', 'Отлов питомца', 'Поймайте боевого питомца.', 'Ты отлично справляешься, $n. Скоро ты и $gсам:сама; не заметишь, как станешь мастером в обращении с питомцами. А теперь пришла пора поймать собственного питомца!$b$bОтправляйся на поиски за город и найди там боевого питомца. Затем вступи с ним в бой, а когда у него останется мало здоровья, брось ловушку.$b$bНе забудь, что твой питомец должен быть минимум 3-го уровня, иначе ловушку нельзя будет использовать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'zhCN', '捉到一只！', '捕获一只战斗宠物。', '你的表现到目前为止都很不错，$n。你在不知不觉中已经成了一个宠物方面的专家，现在是时候去抓一个属于你自己的宠物了！$b$b去外面找一个战斗宠物并与其交战，当它体力不支时抛出你的陷阱。一旦你完成后，记得把捕获的宠物带给我瞧瞧。$b$b记住，你至少要有一个宠物达到了3级才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31584, 'zhTW', '去抓一隻來！', '抓一隻戰寵。', '目前為止你都做得很好，$n！你自己都還沒發覺，你已經要變成一個寵物專家了。現在你該去為自己抓一隻專屬的寵物！$b$b回去找一隻戰寵，與牠交戰，並在牠生命力上限偏低時把你的陷阱丟出去。完成之後，把你抓到的東西帶回來給我看看。$B$B記住，你至少要有一隻戰寵達到等級3才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'deDE', 'Erste Erfahrungen', 'Gewinnt einen Kampf mit einem Haustier.', 'Nachdem Ihr jetzt zum Ausbilden von Tieren qualifiziert seid, ist es Zeit, Eure Fähigkeiten unter Beweis zu stellen!$b$bZieht in die Wildnis und stellt Euch einem Kampfhaustier. Wenn Ihr gewonnen habt, kommt wieder zu mir, dann setze ich Eure Ausbildung fort. Viel Glück!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'esES', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal ahí fuera y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'esMX', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal ahí fuera y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'frFR', 'Apprendre les ficelles', 'Gagnez un combat de mascottes.', 'Maintenant que vous savez dresser les familiers, il est temps de mettre votre talent à l\'épreuve !$b$bAllez dans la nature et attaquez une mascotte de combat. Revenez me voir quand vous aurez gagné, et nous poursuivrons les leçons. Bonne chance !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'itIT', 'Imparare le regole', 'Vinci uno scontro tra mascotte.', 'Ora che sei $gun allenatore:un\'allenatrice; di mascotte, è giunta l\'ora di mettere alla prova le tue abilità!$b$bVai là fuori e affronta una mascotte da combattimento. Torna da me dopo che avrai vinto e continueremo l\'addestramento. Buona fortuna!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'koKR', '요령 익히기', '애완동물 대전에서 승리해야 합니다.', '애완동물 조련사가 되었으니 이제 솜씨를 시험해볼 차례군!$b$b바깥으로 나가서 아무 전투 애완동물이나 골라 싸움을 걸라고. 승리하고 난 뒤 내게 다시 돌아오면 훈련을 계속할 거야. 행운을 빌어!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'ptBR', 'Pegando o jeito', 'Vença uma batalha de mascotes.', 'Agora que você sabe adestrar mascotes, é hora de testar suas habilidades!$B$BSiga pelas matas e enfrente uma mascote de batalha. Volte quando tiver vencido, e eu continuarei seu treinamento. Boa sorte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'ruRU', 'Изучение основ', 'Победите в битве питомцев.', 'Теперь, когда ты умеешь обучать питомцев, пора бы проверить навыки!$b$bОтправляйся на поиски боевых питомцев в округе и вступи в схватку с любым из них. Когда одержишь победу, возвращайся ко мне, и я продолжу обучать тебя. Удачи!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'zhCN', '驯服宠物', '赢得一场宠物战斗。', '既然你是一名宠物训练师，那么是时候检验一下你的专业水准了！$b$b去野外找一只战斗宠物进行决斗。赢了之后回来找我，我会继续你的训练。祝你好运！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31585, 'zhTW', '基礎訓練', '在一場寵物對戰中獲勝。', '現在你已經是個寵物訓練師，是時候測試一下你的技能了！$b$b到野外去挑選一隻戰寵，並與其交戰。獲勝之後就回來找我，我會繼續訓練你。祝你好運！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'deDE', 'Auf dem Wege der Besserung', 'Heilt Eure Haustiere vollständig, indem Ihr Bezzil den Stallmeister aufsucht.', 'Nachdem Ihr nun einen Kampf gewonnen habt, müssen wir Eure Haustiere heilen, damit sie wieder einsatzbereit sind! Sucht unseren Stallmeister Bezzil auf, um Eure Haustiere zu heilen und wiederzubeleben.', '', 'Bezzil, der hiesige Stallmeister, kann Eure Tiere heilen und wiederbeleben.', 'Bezzil', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'esES', 'Mejorando', 'Cura completamente a tus mascotas visitando a Bezzil el maestro de establos.', '¡Ahora que has ganado una batalla, tenemos que curar a tus mascotas para que puedan luchar otro día! Visita a Bezzil, nuestro maestro de establos, para que cure y reviva a tus mascotas.', '', 'Bezzil, el maestro de establos local, puede curar y revivir a tus mascotas.', 'Bezzil', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'esMX', 'Mejorando', 'Cura completamente a tus mascotas visitando a Bezzil el maestro de establos.', '¡Ahora que has ganado una batalla, tenemos que curar a tus mascotas para que puedan luchar otro día! Visita a Bezzil, nuestro maestro de establos, para que cure y reviva a tus mascotas.', '', 'Bezzil, el maestro de establos local, puede curar y revivir a tus mascotas.', 'Bezzil', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'frFR', 'Panser ses plaies', 'Soignez vos mascottes en allant voir Bezzil, le Maître des écuries.', 'Maintenant que vous avez remporté un combat, il faut soigner vos mascottes pour qu\'elles puissent se battre à nouveau ! Allez voir Bezzil, notre maître des écuries, pour soigner et ranimer vos compagnons.', '', 'Bezzil, le maître des écuries des lieux, peut soigner et ranimer vos mascottes.', 'Bezzil', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'itIT', 'Cure intensive', 'Guarisci le ferite delle tue mascotte facendo visita allo stalliere Bezzil.', 'Ora che hai vinto uno scontro, dobbiamo curare le tue mascotte per farle combattere ancora! Vai da Bezzil, il nostro stalliere, per curare o far resuscitare le tue mascotte.', '', 'Bezzil, lo stalliere locale, può curare e resuscitare le tue mascotte.', 'Bezzil', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'koKR', '채찍 다음엔 당근', '야수 관리인 베질을 찾아가 애완동물을 완전히 치유해야 합니다.', '애완동물이 싸움에 이겼으니 다음에도 싸울 수 있도록 치유해줘야 한다고! 이 지역의 야수 관리인인 베질을 찾아가서 애완동물을 회복시키거나 되살려줘.', '', '야수 관리인 베질이 애완동물을 치유하고 되살릴 수 있습니다.', '베질', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'ptBR', 'Cuidados médicos', 'Fale com Bezzil, o Mestre de Estábulo, para curar suas mascotes de batalha completamente.', 'Agora que você venceu uma batalha, precisamos curar suas mascotes para que você possa lutar outro dia! Visite Bezzil, nosso mestre de estábulo, para curar e ressuscitar suas mascotes.', '', 'Bezzil, o Mestre de Estábulo, pode curar e reviver suas mascotes.', 'Bezzil', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'ruRU', 'На поправку', 'Излечите своих питомцев полностью, поговорив со смотрителем стойл Беззилом.', 'Теперь, когда бой выигран, надо подлечить питомцев, чтобы они могли сражаться и дальше! Следует обратиться к смотрителю стойл Беззилу, чтобы вылечить и оживить питомцев.', '', 'Беззил, местный смотритель стойл, может исцелять и оживлять ваших питомцев.', 'Беззил', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'zhCN', '治疗宠物', '拜访兽栏管理员巴兹尔，治疗你的宠物。', '既然你已经赢得了一场战斗，我们就需要治疗你的宠物，这样它们才能继续战斗！拜访一下我们这里的兽栏管理员巴兹尔，来治疗和复活你的宠物。', '', '这里的兽栏管理员巴兹尔可以治愈和复活你的宠物。', '巴兹尔', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31586, 'zhTW', '康復', '去拜訪獸欄管理員比吉爾，以治療你的寵物並恢復其生命力上限。', '現在你在戰鬥中獲勝了，我們必須治癒你的寵物，好讓牠們還有能力再上戰場！去找我們當地的獸欄管理員比吉爾來治療和復活你的寵物。', '', '當地的獸欄管理員比吉爾能夠治癒和復活你的寵物。', '比吉爾', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'deDE', 'Hab\' eins!', 'Fangt ein Kampfhaustier.', 'So weit, so gut, $n! Wenn Ihr so weitermacht, macht Euch bei dem ganzen Haustierkram bald keiner mehr was vor. Jetzt solltet Ihr auch mal ein eigenes Haustier fangen!$b$bZieht wieder los, sucht Euch ein Kampfhaustier, gegen das Ihr kämpfen könnt, greift es an und werft Eure Falle, sobald seine Gesundheit niedrig genug ist. Dann bringt mir Euren Fang zur Begutachtung.$B$BUnd immer dran denken: Ihr braucht mindestens ein Haustier der Stufe 3, um Eure Falle verwenden zu können!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'esES', '¡Tengo una!', 'Atrapa una mascota de duelo.', 'Buen trabajo de momento, $n. Serás $gun:una; profesional de esto de las mascotas antes de que te des cuenta. ¡Es hora de capturar tu propia mascota!$b$bSal de nuevo y encuentra una mascota con la que luchar, enfréntate en duelo con ella y lanza tu trampa cuando su salud esté baja. Cuando termines, tráeme a la mascota que hayas atrapado para que yo la vea.$b$bRecuerda, ¡para utilizar la trampa te hace falta al menos una mascota de nivel 3!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'esMX', '¡Tengo una!', 'Atrapa una mascota de duelo.', 'Buen trabajo de momento, $n. Serás $gun:una; profesional de esto de las mascotas antes de que te des cuenta. ¡Es hora de capturar tu propia mascota!$b$bSal de nuevo y encuentra una mascota con la que luchar, enfréntate en duelo con ella y lanza tu trampa cuando su salud esté baja. Cuando termines, tráeme a la mascota que hayas atrapado para que yo la vea.$b$bRecuerda, ¡para utilizar la trampa te hace falta al menos una mascota de nivel 3!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'frFR', 'J’en ai une !', 'Attrapez une mascotte de combat.', 'Vous faites du bon travail pour l\'instant, $n. Vous serez $gun:une; pro du dressage en moins de temps qu\'il n\'en faut pour le dire. Et maintenant, il est l\'heure d\'attraper votre propre compagnon !$b$bTrouvez une mascotte de combat et attaquez-la, et quand elle sera affaiblie, lancez votre piège. Rapportez-moi votre prise quand vous en aurez une.$b$bN\'oubliez pas qu\'il faut avoir au moins une mascotte de niveau 3 pour pouvoir utiliser le piège !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'itIT', 'Presa!', 'Cattura una Mascotte da Combattimento.', 'Ottimo lavoro finora, $n. Diventerai $gun:una; professionista in men che non si dica. Ora è arrivato il momento di catturare una mascotte!$b$bTorna fuori e trova una Mascotte da Combattimento, affrontala e poi lancia la tua trappola quando l\'avrai indebolita a dovere. Porta la tua preda da me quando avrai finito.$b$bRicorda, devi possedere almeno una mascotte di livello 3 per usare la trappola!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'koKR', '잡았다!', '전투 애완동물을 잡아야 합니다.', '$n, 지금까지 아주 잘했어! 눈 깜짝할 새에 애완동물 다루는 데 전문가가 됐잖아. 이제 네가 애완동물을 직접 잡아볼 차례라고!$b$b다시 나가서 다른 전투 애완동물을 찾아 싸움을 건 다음, 놈의 생명력이 낮아졌을 때 덫을 던져봐. 애완동물을 잡으면 내게 돌아와서 보여줘.$b$b덫을 쓰려는 애완동물이 최소 3 레벨은 돼야 한다는 거 잊지 마!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'ptBR', 'Peguei uma!', 'Capture uma mascote de batalha.', 'Você trabalhou bem até agora, $n. Vai ficar profissional nesse negócio de mascotes rapidinho. Agora é hora de pegar uma mascote para você.$B$BVolte e procure uma mascote de batalha para enfrentar. Jogue sua armadilha quando ela ficar com poucos pontos de vida. Traga-a para mim quando tiver conseguido pegá-la.$b$bLembre-se, você precisa ter ao menos uma mascote em nível 3 para usar a armadilha!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'ruRU', 'Отлов питомца', 'Поймайте боевого питомца.', 'Ты отлично справляешься, $n. Скоро ты и $gсам:сама; не заметишь, как станешь мастером в обращении с питомцами. А теперь пришла пора поймать собственного питомца!$b$bОтправляйся на поиски за город и найди там боевого питомца. Затем вступи с ним в бой, а когда у него останется мало здоровья, брось ловушку.$b$bНе забудь, что твой питомец должен быть минимум 3-го уровня, иначе ловушку нельзя будет использовать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'zhCN', '捉到一只！', '捕获一只战斗宠物。', '你的表现到目前为止都很不错，$n。你在不知不觉中已经成了一个宠物方面的专家，现在是时候去抓一个属于你自己的宠物了！$b$b去外面找一个战斗宠物并与其交战，当它体力不支时抛出你的陷阱。一旦你完成后，记得把捕获的宠物带给我瞧瞧。$b$b记住，你至少要有一个宠物达到了3级才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31587, 'zhTW', '去抓一隻來！', '抓一隻戰寵。', '目前為止你都做得很好，$n！你自己都還沒發覺，你已經要變成一個寵物專家了。現在你該去為自己抓一隻專屬的寵物！$b$b回去找一隻戰寵，與牠交戰，並在牠生命力上限偏低時把你的陷阱丟出去。完成之後，把你抓到的東西帶回來給我看看。$B$B記住，你至少要有一隻戰寵達到等級3才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'deDE', 'Erste Erfahrungen', 'Gewinnt einen Kampf mit einem Haustier.', 'Nachdem Ihr jetzt zum Ausbilden von Tieren qualifiziert seid, ist es Zeit, Eure Fähigkeiten unter Beweis zu stellen!$b$bZieht in die Wildnis und stellt Euch einem Kampfhaustier. Wenn Ihr gewonnen habt, kommt wieder zu mir, dann setze ich Eure Ausbildung fort. Viel Glück!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'esES', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal ahí fuera y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'esMX', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal ahí fuera y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'frFR', 'Apprendre les ficelles', 'Gagnez un combat de mascottes.', 'Maintenant que vous savez dresser les familiers, il est temps de mettre votre talent à l\'épreuve !$b$bAllez dans la nature et attaquez une mascotte de combat. Revenez me voir quand vous aurez gagné, et nous poursuivrons les leçons. Bonne chance !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'itIT', 'Imparare le regole', 'Vinci uno scontro tra mascotte.', 'Ora che sei $gun allenatore:un\'allenatrice; di mascotte, è giunta l\'ora di mettere alla prova le tue abilità!$b$bVai là fuori e affronta una mascotte da combattimento. Torna da me dopo che avrai vinto e continueremo l\'addestramento. Buona fortuna!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'koKR', '요령 익히기', '애완동물 대전에서 승리해야 합니다.', '애완동물 조련사가 되었으니 이제 솜씨를 시험해볼 차례군!$b$b밖으로 나가서 아무 전투 애완동물이나 골라 싸움을 거시오. 승리하고 난 뒤 내게 다시 돌아오면 훈련을 계속할 거요. 행운을 빌겠소!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'ptBR', 'Pegando o jeito', 'Vença uma batalha de mascotes.', 'Agora que você sabe adestrar mascotes, é hora de testar suas habilidades!$B$BSiga pelas matas e enfrente uma mascote de batalha. Volte quando tiver vencido, e eu continuarei seu treinamento. Boa sorte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'ruRU', 'Изучение основ', 'Победите в битве питомцев.', 'Теперь, когда ты умеешь обучать питомцев, пора бы проверить навыки!$b$bОтправляйся на поиски боевых питомцев в округе и вступи в схватку с любым из них. Когда одержишь победу, возвращайся ко мне, и я продолжу обучать тебя. Удачи!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'zhCN', '驯服宠物', '赢得一场宠物战斗。', '既然你是一名宠物训练师，那么是时候检验一下你的专业水准了！$b$b去野外找一只战斗宠物进行决斗。赢了之后回来找我，我会继续你的训练。祝你好运！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31588, 'zhTW', '基礎訓練', '在一場寵物對戰中獲勝。', '現在你已經是個寵物訓練師，是時候測試一下你的技能了！$b$b到野外去挑選一隻戰寵，並與其交戰。獲勝之後就回來找我，我會繼續訓練你。祝你好運！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'deDE', 'Auf dem Wege der Besserung', 'Heilt Eure Haustiere vollständig, indem Ihr Murog den Stallmeister aufsucht.', 'Nachdem Ihr nun einen Kampf gewonnen habt, müssen wir Eure Haustiere heilen, damit sie wieder einsatzbereit sind! Sucht unseren Stallmeister Murog auf, um Eure Haustiere zu heilen und wiederzubeleben.', '', 'Murog, der hiesige Stallmeister, kann Eure Tiere heilen und wiederbeleben.', 'Murog', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'esES', 'Mejorando', 'Cura completamente a tus mascotas visitando a Murog el maestro de establos.', '¡Ahora que has ganado una batalla, tenemos que curar a tus mascotas para que puedan luchar otro día! Visita a Murog, nuestro maestro de establos, para que cure y reviva a tus mascotas.', '', 'Murog, el maestro de establos local, puede curar y revivir a tus mascotas.', 'Murog', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'esMX', 'Mejorando', 'Cura completamente a tus mascotas visitando a Murog el maestro de establos.', '¡Ahora que has ganado una batalla, tenemos que curar a tus mascotas para que puedan luchar otro día! Visita a Murog, nuestro maestro de establos, para que cure y reviva a tus mascotas.', '', 'Murog, el maestro de establos local, puede curar y revivir a tus mascotas.', 'Murog', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'frFR', 'Panser ses plaies', 'Soignez vos mascottes en allant voir Murog, le Maître des écuries.', 'Maintenant que vous avez remporté un combat, il faut soigner vos mascottes pour qu\'elles puissent se battre à nouveau ! Allez voir Murog, notre maître des écuries, pour soigner et ranimer vos compagnons.', '', 'Murog, le maître des écuries des lieux, peut soigner et ranimer vos mascottes.', 'Murog', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'itIT', 'Cure intensive', 'Guarisci le ferite delle tue mascotte facendo visita allo stalliere Murog.', 'Ora che hai vinto uno scontro, dobbiamo curare le tue mascotte per farle combattere ancora! Vai da Murog, il nostro stalliere, per curare o far resuscitare le tue mascotte.', '', 'Murog, lo stalliere locale, può curare e resuscitare le tue mascotte.', 'Murog', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'koKR', '채찍 다음엔 당근', '야수 관리인 무로그를 찾아가 애완동물을 완전히 치유해야 합니다.', '애완동물이 싸움에 이겼으니 다음에도 싸울 수 있도록 치유해줘야 하오! 이 지역의 야수 관리인인 무로그를 찾아가서 애완동물을 회복시키거나 되살려주시오.', '', '야수 관리인 무로그가 애완동물을 치유하고 되살릴 수 있습니다.', '무로그', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'ptBR', 'Cuidados médicos', 'Fale com Murog, o Mestre de Estábulo, para curar suas mascotes de batalha completamente.', 'Agora que você venceu uma batalha, precisamos curar suas mascotes para que você possa lutar outro dia! Visite Murog, nosso mestre de estábulo, para curar e ressuscitar suas mascotes.', '', 'Murog, o Mestre de Estábulo, pode curar e reviver suas mascotes.', 'Murog', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'ruRU', 'На поправку', 'Излечите своих питомцев полностью, поговорив со смотрителем стойл Мурогом.', 'Теперь, когда бой выигран, надо подлечить питомцев, чтобы они могли сражаться и дальше! Следует обратиться к смотрителю стойл Мурогу, чтобы вылечить и оживить питомцев.', '', 'Мурог, местный смотритель стойл, может исцелять и оживлять ваших питомцев.', 'Мурог', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'zhCN', '治疗宠物', '拜访兽栏管理员姆罗格，治疗你的宠物。', '既然你已经赢得了一场战斗，我们就需要治疗你的宠物，这样它们才能继续战斗！拜访一下我们这里的兽栏管理员姆罗格，来治疗和复活你的宠物。', '', '这里的兽栏管理员姆罗格可以治愈和复活你的宠物。', '姆罗格', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31589, 'zhTW', '康復', '去拜訪獸欄管理員穆羅格，以治療你的寵物並恢復其生命力上限。', '現在你在戰鬥中獲勝了，我們必須治癒你的寵物，好讓牠們還有能力再上戰場！去找我們當地的獸欄管理員穆羅格來治療和復活你的寵物。', '', '當地的獸欄管理員穆羅格能夠治癒和復活你的寵物。', '穆羅格', '', '', '', 0);
