INSERT INTO `quest_template_locale` VALUES (31590, 'ruRU', 'Отлов питомца', 'Поймайте боевого питомца.', 'Ты отлично справляешься, $n. Скоро ты и $gсам:сама; не заметишь, как станешь мастером в обращении с питомцами. А теперь пришла пора поймать собственного питомца!$b$bОтправляйся на поиски за город и найди там боевого питомца. Затем вступи с ним в бой, а когда у него останется мало здоровья, брось ловушку.$b$bНе забудь, что твой питомец должен быть минимум 3-го уровня, иначе ловушку нельзя будет использовать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31590, 'zhCN', '捉到一只！', '捕获一只战斗宠物。', '你的表现到目前为止都很不错，$n。你在不知不觉中已经成了一个宠物方面的专家，现在是时候去抓一个属于你自己的宠物了！$b$b去外面找一个战斗宠物并与其交战，当它体力不支时抛出你的陷阱。一旦你完成后，记得把捕获的宠物带给我瞧瞧。$b$b记住，你至少要有一个宠物达到了3级才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31590, 'zhTW', '去抓一隻來！', '抓一隻戰寵。', '目前為止你都做得很好，$n！你自己都還沒發覺，你已經要變成一個寵物專家了。現在你該去為自己抓一隻專屬的寵物！$b$b回去找一隻戰寵，與牠交戰，並在牠生命力上限偏低時把你的陷阱丟出去。完成之後，把你抓到的東西帶回來給我看看。$B$B記住，你至少要有一隻戰寵達到等級3才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'deDE', 'Erste Erfahrungen', 'Gewinnt einen Kampf mit einem Haustier.', 'Nachdem Ihr jetzt zum Ausbilden von Tieren qualifiziert seid, ist es Zeit, Eure Fähigkeiten unter Beweis zu stellen!$b$bZieht in die Wildnis und stellt Euch einem Kampfhaustier. Wenn Ihr gewonnen habt, kommt wieder zu mir, dann setze ich Eure Ausbildung fort. Viel Glück!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'esES', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal ahí fuera y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'esMX', 'Aprendizaje inicial', 'Gana un duelo de mascotas.', '¡Ahora que eres $ginstructor:instructora; de mascotas, es hora de poner a prueba tus habilidades!$b$bSal ahí fuera y entabla un duelo con una mascota. Vuelve cuando hayas ganado y continuaré tu instrucción. ¡Buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'frFR', 'Apprendre les ficelles', 'Gagnez un combat de mascottes.', 'Maintenant que vous savez dresser les familiers, il est temps de mettre votre talent à l\'épreuve !$b$bAllez dans la nature et attaquez une mascotte de combat. Revenez me voir quand vous aurez gagné, et nous poursuivrons les leçons. Bonne chance !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'itIT', 'Imparare le regole', 'Vinci uno scontro tra mascotte.', 'Ora che sei $gun allenatore:un\'allenatrice; di mascotte, è giunta l\'ora di mettere alla prova le tue abilità!$b$bVai là fuori e affronta una mascotte da combattimento. Torna da me dopo che avrai vinto e continueremo l\'addestramento. Buona fortuna!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'koKR', '요령 익히기', '애완동물 대전에서 승리해야 합니다.', '애완동물 조련사가 되었으니 이제 솜씨를 시험해볼 차례군요!$b$b숲으로 나가서 아무 전투 애완동물이나 골라 싸움을 거세요. 승리하고 난 뒤 제게 다시 돌아오시면 훈련을 계속할 거예요. 행운을 빌어요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'ptBR', 'Pegando o jeito', 'Vença uma batalha de mascotes.', 'Agora que você sabe adestrar mascotes, é hora de testar suas habilidades!$B$BSiga pelas matas e enfrente uma mascote de batalha. Volte quando tiver vencido, e eu continuarei seu treinamento. Boa sorte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'ruRU', 'Изучение основ', 'Победите в битве питомцев.', 'Теперь, когда ты умеешь обучать питомцев, пора бы проверить навыки!$b$bОтправляйся на поиски боевых питомцев в округе и вступи в схватку с любым из них. Когда одержишь победу, возвращайся ко мне, и я продолжу обучать тебя. Удачи!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'zhCN', '驯服宠物', '赢得一场宠物战斗。', '既然你是一名宠物训练师，那么是时候检验一下你的专业水准了！$b$b去野外找一只战斗宠物进行决斗。赢了之后回来找我，我会继续你的训练。祝你好运！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31591, 'zhTW', '基礎訓練', '在一場寵物對戰中獲勝。', '現在你已經是個寵物訓練師，是時候測試一下你的技能了！$b$b到野外去挑選一隻戰寵，並與其交戰。獲勝之後就回來找我，我會繼續訓練你。祝你好運！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'deDE', 'Auf dem Wege der Besserung', 'Heilt Eure Haustiere vollständig, indem Ihr Jenova Steinschild die Stallmeisterin aufsucht.', 'Nachdem Ihr nun einen Kampf gewonnen habt, müssen wir Eure Haustiere heilen, damit sie wieder einsatzbereit sind! Sucht unsere Stallmeisterin Jenova Steinschild auf, um Eure Haustiere zu heilen und wiederzubeleben.', '', 'Jenova Steinschild, die hiesige Stallmeisterin, kann Eure Tiere heilen und wiederbeleben.', 'Jenova Steinschild', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'esES', 'Mejorando', 'Sana tus mascotas por completo visitando a Jenova Petrescudo, la maestra de establos.', 'Ahora que ya has ganado tu primer duelo, necesitamos sanar a tus mascotas para que puedan seguir luchando. Visita a Jenova Petrescudo, nuestra maestra de establos local, para sanar y revivir a tus mascotas.', '', 'Jenova Petrescudo, la maestra de establos local, puede sanar y revivir a tus mascotas.', 'Jenova Petrescudo', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'esMX', 'Mejorando', 'Sana tus mascotas por completo visitando a Jenova Petrescudo, la maestra de establos.', 'Ahora que ya has ganado tu primer duelo, necesitamos sanar a tus mascotas para que puedan seguir luchando. Visita a Jenova Petrescudo, nuestra maestra de establos local, para sanar y revivir a tus mascotas.', '', 'Jenova Petrescudo, la maestra de establos local, puede sanar y revivir a tus mascotas.', 'Jenova Petrescudo', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'frFR', 'Panser ses plaies', 'Soignez vos mascottes en allant voir Jenova Targe-pierre, le Maître des écuries.', 'Maintenant que vous avez remporté un combat, il faut soigner vos mascottes pour qu\'elles puissent se battre à nouveau ! Allez voir Jenova Targe-pierre, notre maître des écuries, pour soigner et ranimer vos compagnons.', '', 'Jenova Targe-pierre, le maître des écuries des lieux, peut soigner et ranimer vos mascottes.', 'Jenova Targe-Pierre', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'itIT', 'Cure intensive', 'Guarisci le ferite delle tue mascotte facendo visita alla stalliera Jenova Scudosaldo.', 'Ora che hai vinto uno scontro, dobbiamo curare le tue mascotte per farle combattere ancora! Vai da Jenova Scudosaldo, la nostra stalliera, per curare o far resuscitare le tue mascotte.', '', 'Jenova Scudosaldo, la stalliera locale, può curare e resuscitare le tue mascotte.', 'Jenova Scudosaldo', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'koKR', '채찍 다음엔 당근', '야수 관리인 제노바 스톤실드를 찾아가 애완동물을 완전히 치유해야 합니다.', '애완동물이 싸움에 이겼으니 다음에도 싸울 수 있도록 치유해줘야 해요! 이 지역의 야수 관리인인 제노바 스톤실드를 찾아가서 애완동물을 회복시키거나 되살려주세요.', '', '야수 관리인 제노바 스톤실드가 애완동물을 치유하고 되살릴 수 있습니다.', '제노바 스톤실드', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'ptBR', 'Cuidados médicos', 'Fale com Jenova Escudopétreo, a Mestre de Estábulo, para curar suas mascotes de batalha completamente.', 'Agora que você venceu uma luta, você precisa curar suas mascotes para elas ficarem em forma de novo! Fale com Jenova Escudopétreo, nossa mestre de estábulo, para curar e reviver suas mascotes.', '', 'Jenova Escudopétreo, a Mestre de Estábulo, pode curar e reviver suas mascotes.', 'Jenova Escudopétreo', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'ruRU', 'На поправку', 'Излечите своих питомцев полностью, поговорив со смотрительницей стойл Дженовой Камнещит.', 'Теперь, когда бой выигран, надо подлечить питомцев, чтобы они могли сражаться и дальше! Следует обратиться к смотрителю стойл Дженове Камнещит, чтобы вылечить и оживить питомцев.', '', 'Дженова Камнещит, местная смотрительница стойл, может исцелять и оживлять ваших питомцев.', 'Дженова Камнещит', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'zhCN', '治疗宠物', '拜访兽栏管理员耶诺瓦·石盾，治疗你的宠物。', '既然你已经赢得了一场战斗，我们就需要治疗你的宠物，这样它们才能继续战斗！拜访一下我们这里的兽栏管理员耶诺瓦·石盾，来治疗和复活你的宠物。', '', '这里的兽栏管理员耶诺瓦·石盾可以治愈和复活你的宠物。', '耶诺瓦·石盾', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31592, 'zhTW', '康復', '去拜訪獸欄管理員耶諾瓦‧石盾，以治療你的寵物並恢復其生命力上限。', '現在你在戰鬥中獲勝了，我們必須治癒你的寵物，好讓牠們還有能力再上戰場！去找我們當地的獸欄管理耶諾瓦‧石盾來治療和復活你的寵物。', '', '當地的獸欄管理員耶諾瓦‧石盾能夠治癒和復活你的寵物。', '耶諾瓦‧石盾', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'deDE', 'Hab\' eins!', 'Fangt ein Kampfhaustier.', 'So weit, so gut, $n! Wenn Ihr so weitermacht, macht Euch bei dem ganzen Haustierkram bald keiner mehr was vor. Jetzt solltet Ihr auch mal ein eigenes Haustier fangen!$b$bZieht wieder los, sucht Euch ein Kampfhaustier, gegen das Ihr kämpfen könnt, greift es an und werft Eure Falle, sobald seine Gesundheit niedrig genug ist. Dann bringt mir Euren Fang zur Begutachtung.$B$BUnd immer dran denken: Ihr braucht mindestens ein Haustier der Stufe 3, um Eure Falle verwenden zu können!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'esES', '¡Tengo una!', 'Consigue una mascota de duelo.', 'Buen trabajo de momento, $n. Serás $gun:una; profesional de esto de las mascotas antes de que te des cuenta. ¡Es hora de capturar tu propia mascota!$b$bSal de nuevo y encuentra una mascota con la que luchar, enfréntate en duelo con ella y lanza tu trampa cuando su salud esté baja. Cuando termines, tráeme a la mascota que hayas atrapado para que yo la vea.$b$bRecuerda, ¡para utilizar la trampa te hace falta al menos una mascota de nivel 3!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'esMX', '¡Tengo una!', 'Consigue una mascota de duelo.', 'Buen trabajo de momento, $n. Serás $gun:una; profesional de esto de las mascotas antes de que te des cuenta. ¡Es hora de capturar tu propia mascota!$b$bSal de nuevo y encuentra una mascota con la que luchar, enfréntate en duelo con ella y lanza tu trampa cuando su salud esté baja. Cuando termines, tráeme a la mascota que hayas atrapado para que yo la vea.$b$bRecuerda, ¡para utilizar la trampa te hace falta al menos una mascota de nivel 3!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'frFR', 'J’en ai une !', 'Attrapez une mascotte de combat.', 'Vous faites du bon travail pour l\'instant, $n. Vous serez $gun:une; pro du dressage en moins de temps qu\'il n\'en faut pour le dire. Et maintenant, il est l\'heure d\'attraper votre propre compagnon !$b$bTrouvez une mascotte de combat et attaquez-la, et quand elle sera affaiblie, lancez votre piège. Rapportez-moi votre prise quand vous en aurez une.$b$bN\'oubliez pas qu\'il faut avoir au moins une mascotte de niveau 3 pour pouvoir utiliser le piège !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'itIT', 'Presa!', 'Cattura una Mascotte da combattimento.', 'Ottimo lavoro finora, $n. Diventerai $gun:una; professionista in men che non si dica. Ora è arrivato il momento di catturare una mascotte!$b$bTorna fuori e trova una Mascotte da Combattimento, affrontala e poi lancia la tua trappola quando l\'avrai indebolita a dovere. Porta la tua preda da me quando avrai finito.$b$bRicorda, devi possedere almeno una mascotte di livello 3 per usare la trappola!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'koKR', '잡았다!', '전투 애완동물을 잡아야 합니다.', '$n 님, 지금까지 아주 잘하셨어요! 눈 깜짝할 새에 애완동물 다루는 데 전문가가 되셨으니까요. 이제 $n 님의 애완동물을 직접 잡아볼 차례랍니다!$b$b다시 나가서 다른 전투 애완동물을 찾아 싸움을 건 다음, 놈의 생명력이 낮아졌을 때 덫을 던지면 돼요. 애완동물을 잡으면 제게 돌아와서 보여주시고요.$b$b덫을 쓰려는 애완동물이 최소 3 레벨은 돼야 한다는 걸 기억하세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31593, 'ptBR', 'Peguei uma!', 'Capture uma mascote de batalha.', 'Você trabalhou bem até agora, $n. Vai ficar profissional nesse negócio de mascotes rapidinho. Agora é hora de pegar uma mascote para você.$B$BVolte e procure uma mascote de batalha para enfrentar. Jogue sua armadilha quando ela ficar com poucos pontos de vida. Traga-a para mim quando tiver conseguido pegá-la.$b$bLembre-se, você precisa ter ao menos uma mascote em nível 3 para usar a armadilha!', '', '', '', '', '', '', 0);
