INSERT INTO `quest_template_locale` VALUES (31593, 'zhTW', '去抓一隻來！', '抓一隻戰寵。', '目前為止你都做得很好，$n！你自己都還沒發覺，你已經要變成一個寵物專家了。現在你該去為自己抓一隻專屬的寵物！$b$b回去找一隻戰寵，與牠交戰，並在牠生命力上限偏低時把你的陷阱丟出去。完成之後，把你抓到的東西帶回來給我看看。$B$B記住，你至少要有一隻戰寵達到等級3才能使用陷阱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'deDE', 'Kypa\'raks Kern', 'Bringt den massiven Kyparitkern zu Bernschmied Zikk in Klaxxi\'vess.', '<Als der Ringwurm auseinanderbricht, fällt aus seinem Innersten ein gewaltiger Klumpen organischen Kyparits heraus. Das wird jemand in Klaxxi\'vess sehen wollen.>', '', '', '', '', '', 'Bringt den massiven Kyparitkern zu Bernschmied Zikk in Klaxxi\'vess.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'esES', 'El núcleo de Kypa\'rak', 'Lleva el núcleo de kyparita macizo al herrero de ámbar Zikk en Klaxxi\'vess.', '<Cuando la tiña se derrumba, un trozo enorme de kyparita orgánica cae de su núcleo. Alguien en Klaxxi\'vess querrá verlo.>', '', '', '', '', '', 'Lleva el núcleo de kyparita macizo al herrero de ámbar Zikk, en Klaxxi\'vess.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'esMX', 'El núcleo de Kypa\'rak', 'Lleva el núcleo de kyparita macizo al herrero de ámbar Zikk en Klaxxi\'vess.', '<Cuando la tiña se derrumba, un trozo enorme de kyparita orgánica cae de su núcleo. Alguien en Klaxxi\'vess querrá verlo.>', '', '', '', '', '', 'Lleva el núcleo de kyparita macizo al herrero de ámbar Zikk, en Klaxxi\'vess.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'frFR', 'Le noyau de Kypa’rak', 'Apportez le Noyau de kyparite massif à l’Ambre-forgeron Zikk à Klaxxi’vess.', '<Lorsque la teigne s’effondre, un énorme morceau de kyparite organique tombe de son corps. Il intéressera forcément quelqu’un à Klaxxi’vess.>', '', '', '', '', '', 'Apportez le Noyau de kyparite massif à l’Ambre-forgeron Zikk à Klaxxi’vess.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'itIT', 'Il nucleo di Kypa\'rak', 'Porta il Nucleo di Kyparite Massiccio al Fabbro d\'Ambra Zikk a Klaxxi\'vess.', '<Non appena la bestia si spacca, dal suo corpo esce un grosso mucchio di kyparite. Qualcuno a Klaxxi\'vess potrebbe volerlo.>', '', '', '', '', '', 'Porta il Nucleo di Kyparite Massiccio al Fabbro d\'Ambra Zikk a Klaxxi\'vess.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'koKR', '키파라크의 핵', '클락시베스에 있는 호박대장장이 지크에게 거대한 키파라이트 핵을 가져가야 합니다.', '<고리 벌레가 산산조각 나자, 그 핵에서 커다란 생체 키파라이트 덩어리가 떨어집니다. 클락시베스에 있는 누군가 이걸 보고 싶어할 겁니다.>', '', '', '', '', '', '클락시베스에 있는 호박대장장이 지크에게 거대한 키파라이트 핵을 가져가야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'ptBR', 'O núcleo de Kypa\'rak', 'Leve o Núcleo Maciço de Kyparita para o Molda-âmbar Zikk em Klaxxi\'vess.', '<À medida que o verme se despedaça, um pedaço maciço de kyparita orgânica cai de seu núcleo. Deve alguém em Klaxxi\'vess interessado em ver isto.>', '', '', '', '', '', 'Leve o Núcleo Maciço de Kyparita para o Molda-âmbar Zikk em Klaxxi\'vess.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'ruRU', 'Ядро Кипа\'рака', 'Принесите большое кипаритовое ядро кователю янтаря Зикку в Клакси\'весс.', '<Когда червь разваливается на части, из его сердцевины выпадает большой кусок кипарита. Кое-кого в Клакси\'вессе он обязательно заинтересует.>', '', '', '', '', '', 'Принесите большое кипаритовое ядро кователю янтаря Зикку в Клакси\'весс.', 0);
INSERT INTO `quest_template_locale` VALUES (31598, 'zhCN', '凯帕拉克之核', '把巨型凯帕琥珀核心交给卡拉克西维斯的琥珀匠师齐克。', '<白斑破裂之后，一大块有机凯帕琥珀从里面掉了出来。卡拉克西维斯应该有人对这个东西感兴趣。>', '', '', '', '', '', '把巨型凯帕琥珀核心交给卡拉克西维斯的琥珀匠师齐克。', 0);
