INSERT INTO `quest_template_locale` VALUES (31606, 'frFR', 'Le Disséqueur s\'éveille', 'Repoussez les Zan\'thik pendant que Rik\'kal s\'éveille.', 'Vous n\'êtes… pas comme les autres…$b$bJ\'entends l\'appel des klaxxi… <il tousse.>$b$bLaissez-moi un instant.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31606, 'itIT', 'Il risveglio di Rik\'kal', 'Tieni a bada gli Zan\'thik mentre Rik\'kal si risveglia.', 'Tu... tu non sei come gli altri...$B$BSento il richiamo dei Klaxxi...<Tossisce>$B$BHo solo bisogno di un attimo per raccogliere le forze.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31606, 'koKR', '해부자, 깨어나다', '리크칼이 정신을 차리는 동안 잔티크 사마귀 무리를 막아야 합니다.', '너는... 다른 이들과는 다르군...$B$B클락시의 부름을 들었는데... <콜록>$B$B잠깐 시간을 다오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31606, 'ptBR', 'O Dissecador acorda', 'Rechace os Zan\'thik enquanto Rik\'kal desperta.', 'Você não... é como os outros...$B$BOuço os Klaxxi chamando... <tosse>$B$BConceda-me apenas alguns instantes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31606, 'ruRU', 'Пробуждение Рик\'кала', 'Сдерживайте зан\'тиков, пока Рик\'кал не придет в себя.', 'Ты... не похож на них...$B$BЯ слышу зов клакси... <кашляет>$B$BДай мне прийти в себя.', '', '', '', '', '', '', 0);
