INSERT INTO `quest_template_locale` VALUES (31693, 'frFR', 'Julia Stevens', 'Gagnez un combat de mascottes contre Julia Stevens.', 'Vous pensez que vous êtes $gprêt:prête; à m’affronter à nouveau ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31693, 'itIT', 'Julia Stevens', 'Sconfiggi Julia Stevens in uno scontro tra mascotte.', 'Pensi di potermi sconfiggere ancora, eh?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31693, 'koKR', '줄리아 스티븐스', '애완동물 대전에서 줄리아 스티븐스를 꺾어야 합니다.', '저를 또 이길 수 있을 것 같아요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31693, 'ptBR', 'Júlia Esteves', 'Vença Júlia Esteves numa batalha de mascotes.', 'Você acha que me aguenta de novo?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31693, 'ruRU', 'Джулия Стивенс', 'Победите Джулию Стивенс в битве питомцев.', 'Думаешь, что сможешь снова меня одолеть?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31693, 'zhCN', '茱莉亚·史蒂文斯', '在宠物战斗中击败茱莉亚·史蒂文斯。', '你觉得你这次还能赢？', '', '', '', '', '', '', 0);
