INSERT INTO `quest_template_locale` VALUES (31809, 'frFR', 'Les vestiges de la colère', 'Apportez la Griffe de la colère à Ban Cœur d\'Ours à la Garnison des Pandashan dans les Steppes de Tanglong.', 'Vous contemplez la griffe arrachée au sha de la colère, et vous vous rendez compte que Ban Cœur d\'Ours, commandant des pandashan des steppes de Tanglong, serait heureux d\'apprendre votre victoire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31809, 'itIT', 'Resti della Rabbia', 'Consegna l\'Artiglio di Rabbia a Ban Cuor Forte alla Guarnigione degli Shandaren nelle Steppe di Tong Long.', '<Osservando l\'artiglio amputato dello sconfitto Sha della Rabbia, reputi che Ban Cuor Forte, il Comandante degli Shandaren nelle Steppe di Tong Long, sarebbe interessato a sapere della tua vittoria.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31809, 'koKR', '분노의 잔재', '탕랑 평원의 음영파 주둔지에 있는 반 베어하트에게 분노의 발톱을 가져가야 합니다.', '쓰러진 분노의 샤의 잘린 발톱을 보자 탕랑 평원의 음영파 주둔지 사령관인 반 베어하트에게 소식을 전해야겠다는 생각이 듭니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31809, 'ptBR', 'Restos de Ira', 'Entregue a Garra da Ira a Ban Coração de Urso na Guarnição Shado-Pan, que fica nas Estepes de Taolong.', 'Você contempla a garra decepada do Sha da Ira derrotado, e lembra que Ban Coração de Urso, comandante dos Shado-Pan nas Estepes de Taolong, vai querer notícias da sua vitória.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31809, 'ruRU', 'Остатки злости', 'Доставьте Коготь гнева Баню Медвежьему Сердцу, который находится в Танлунских степях, в гарнизоне Шадо-Пан.', 'Глядя на отрубленный коготь лапы Ша Злости, вы понимаете, что о вашей победе непременно захочет узнать Бань Медвежье Сердце, командир монахов Шадо-Пан в Танлунских степях.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31809, 'zhCN', '怒之煞的残肢', '将愤怒之爪带给螳螂高原影踪卫戍营的班·熊心。', '当你看着被击败的怒之煞所掉落的残爪时，你突然意识到应该把这个胜利的好消息带给螳螂高原的影踪派指挥官班·熊心。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31809, 'zhTW', '憤怒的遺骸', '將憤怒之爪帶去螳螂荒原的影潘要塞給班‧熊心。', '看著憤怒之煞被擊敗後所遺留的斷爪，你突然想到螳螂荒原的影潘指揮官班‧熊心應該會想知道你勝利的消息。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'deDE', 'Am Himmel reiten', 'Schließt Euch dem Orden der Wolkenschlange an.', 'Ich kann Euch nichts mehr beibringen, $n.$B$BIhr habt Euch Eurer Schlange als würdig erwiesen.$B$BHeute werdet Ihr in unseren Orden aufgenommen. Seid Ihr bereit?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'esES', 'Surcar los cielos', 'Únete a la Orden del Dragón Nimbo.', 'Ya no puedo considerarme tu maestro, $n.$B$BHas demostrado ser $gmerecedor:merecedora; de tu dragón nimbo.$B$BHoy te unirás a nuestras filas. ¿Estás $gpreparado:preparada;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'esMX', 'Surcar los cielos', 'Únete a la Orden del Dragón Nimbo.', 'Ya no puedo considerarme tu maestro, $n.$B$BHas demostrado ser $gmerecedor:merecedora; de tu dragón nimbo.$B$BHoy te unirás a nuestras filas. ¿Estás $gpreparado:preparada;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'frFR', 'Chevaucher dans les cieux', 'Rejoignez l’Ordre du Serpent-nuage.', 'Je ne peux plus me considérer comme votre professeur, $n.$B$BVous avez su vous montrer digne de votre serpent-nuage.$B$BAujourd’hui, vous allez rejoindre nos rangs. Êtes-vous $gprêt:prête; ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'itIT', 'A cavallo nei cieli', 'Unisciti all\'Ordine della Serpe delle Nubi.', '$n, non ho più nulla da insegnarti.$B$BTi sei $gdimostrato degno:dimostrata degna; della tua serpe delle nubi.$B$BDa oggi puoi unirti ai nostri ranghi. Sei $gpronto:pronta;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'koKR', '하늘을 달려', '운룡단의 일원이 되어야 합니다.', '이제 난 자네의 스승이라고 할 수가 없겠네, $n.$B$B자네는 운룡을 탈 자격이 있음을 스스로 증명해 보였어.$B$B오늘, 자네는 우리와 하나가 될 걸세. 준비 됐나?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'ptBR', 'Cortando os céus', 'Junte-se à Ordem da Serpente das Nuvens', 'Já não posso mais dizer que sou sua professora, $n.$B$BVocê provou que é dign$Go:a; de sua serpente das nuvens.$B$BHoje, você se junta às nossas fileiras. Está preparad$Go:a;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'ruRU', 'Полет Облачного Змея', 'Вступите в орден Облачного Змея.', 'Я более не могу называть себя твоим учителем, $n.$B$BТы $gпоказал себя достойным:показала себя достойной; своего облачного змея.$B$BСегодня мы примем тебя в свои ряды. Ты $gготов:готова; к этому?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'zhCN', '展翅高飞', '加入云端翔龙骑士团。', '我不能再以你的老师自居了，$n。$B$B你已经证明了自己，你的确配得上你的云端翔龙。$B$B今天，你将加入到我们的行列。准备好了吗?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31810, 'zhTW', '天翔', '加入雲蛟衛。', '我能教你的都已經講完了，$n。$B$B你已證明自己有資格擔任你雲蛟的主人。$B$B今天，你將成為我們的一份子。你準備好了嗎?', '', '', '', '', '', '', 0);
