INSERT INTO `quest_template_locale` VALUES (31819, 'deDE', 'Dagra die Grimmige', 'Besiegt Dagra die Grimmige in einem Haustierkampf.', 'Ihr seid diesen Berg erneut bestiegen, nur, um Eurem eigenen Untergang beizuwohnen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'esES', 'Dagra la Fiera', 'Derrota a Dagra la Fiera en un duelo de mascotas.', 'Veo que has vuelto a subir esta colina, y todo para enfrentarte a tu propia derrota.', '', '', '', '', '', '', 0);
