INSERT INTO `quest_template_locale` VALUES (31819, 'frFR', 'Dagra la Féroce', 'Gagnez un combat de mascottes contre Dagra la Féroce.', 'Vous n’avez escaladé cette montagne à nouveau que pour rencontrer la mort.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'itIT', 'Dagra l\'Implacabile', 'Sconfiggi Dagra l\'Implacabile in uno scontro tra mascotte.', 'Hai scalato questa montagna solo per affrontare la tua sconfitta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'koKR', '맹렬한 다그라', '애완동물 대전에서 맹렬한 다그라를 꺾어야 합니다.', '이 산을 다시 한 번 오른 건, 죽음과 맞서기 위해서겠지.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'ptBR', 'Dagra, a Bruta', 'Derrote Dagra, a Bruta, numa batalha de mascotes.', 'Você escalou esta montanha outra vez apenas para morrer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'ruRU', 'Дагра Яростная', 'Победите Дагру Яростную в битве питомцев.', 'В этот раз ты встретишь на горе собственную смерть.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'zhCN', '凶狠的达戈', '在宠物战斗中击败凶狠的达戈。', '你又上山了，这次便叫你有来无回。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31819, 'zhTW', '『好勇鬥狠』達格拉', '在寵物對戰中擊敗『好勇鬥狠』達格拉。', '你再次登上這座山，得到的只會是你的滅亡。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'deDE', 'Ein Geschenk für den Lehrer', 'Nehmt Nomis Geschenk an.', 'Ich habe jetzt mit den anderen Kindern aufgeholt, obwohl ich erst so spät angefangen habe. Endlich kann ich am planmäßigen Unterricht teilnehmen!$b$bIch habe Euch eine Schürze gemacht, um mich für all das zu bedanken, was Ihr für mich getan habt. Ich hoffe, sie gefällt Euch.', '', '', '', '', '', 'Nehmt Nomis Geschenk an.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'esES', 'Un regalo para el profe', 'Recibe el regalo de Nomi.', 'Estoy al mismo nivel que el resto de los chicos a pesar de que empecé muy tarde. ¡Ya puedo ir a las clases normales!$B$BTe he hecho un delantal para demostrarte cuánto aprecio lo que has hecho por mí. Espero que te guste.', '', '', '', '', '', 'Recibe el regalo de Nomi.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'esMX', 'Un regalo para el profe', 'Recibe el regalo de Nomi.', 'Estoy al mismo nivel que el resto de los chicos a pesar de que empecé muy tarde. ¡Ya puedo ir a las clases normales!$B$BTe he hecho un delantal para demostrarte cuánto aprecio lo que has hecho por mí. Espero que te guste.', '', '', '', '', '', 'Recibe el regalo de Nomi.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'frFR', 'Un cadeau pour le professeur', 'Acceptez le cadeau de Nomi.', 'Je suis arrivé au même niveau que les autres enfants, même si je m’y suis mis sur le tard. Maintenant, je peux suivre les cours normaux !$b$bJe vous ai fabriqué un tablier pour vous exprimer toute ma reconnaissance. Vous l’aimez, j’espère ?', '', '', '', '', '', 'Acceptez le cadeau de Nomi.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'itIT', 'Un regalo per il maestro', 'Ricevi il dono di Nomi.', 'Sono tornato in pari con gli altri ragazzi, anche se ho cominciato più tardi. Ora posso seguire le loro stesse lezioni!$b$bHo preparato un grembiule per mostrarti quanto ho apprezzato il tuo aiuto. Ti piace?', '', '', '', '', '', 'Ricevi il regalo di Nomi.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'koKR', '선생님을 위한 선물', '노미의 선물을 받아야 합니다.', '제일 늦게 시작했는데도 이제 다른 아이들을 거의 다 따라잡았어요. 이제 정규 수업을 들을 수 있겠어요!$b$b선생님께 고마움을 표시하려고 앞치마를 만들어 봤어요. 마음에 드셔야 할 텐데.', '', '', '', '', '', '노미의 선물 받기', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'ptBR', 'Um presente inesperado', 'Receba o presente de Nomi.', 'Comecei muito atrasado, mas agora já estou no mesmo nível dos outros alunos. Agora eu posso assistir às aulas normais!$B$BEu fiz um avental para lhe mostrar o quanto estou grato por tudo que você fez por mim. Espero que goste!', '', '', '', '', '', 'Receba o presente de Nomi.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'ruRU', 'Подарок для учителя', 'Получите подарок от Номи.', 'Я полностью догнал других детей, даже несмотря на то, что начал гораздо позже. И теперь я могу посещать обычные уроки вместе со всеми!$b$bЯ сделал для тебя фартук, чтобы показать, как я ценю все то, что ты для меня $gсделал:сделала;. Надеюсь, тебе нравится?', '', '', '', '', '', 'Получите подарок от Номи.', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'zhCN', '谢师礼', '接受诺米的礼物。', '虽然起步比较晚，可我现在已经赶上其他孩子的进度啦。现在我可以正常上课了！$b$b为了感谢你为我做的一切，我给你做了一条围裙。但愿你能喜欢。', '', '', '', '', '', '接受诺米的礼物。', 0);
INSERT INTO `quest_template_locale` VALUES (31820, 'zhTW', '給老師的禮物', '收下糯米的禮物。', '儘管我開始得很晚，但我已經趕上其他孩子了。現在我可以加入常規班了！$b$b我為你做了一件圍裙，來讓你知道我有多麼感激你所做的事。希望你喜歡？', '', '', '', '', '', '收下糯米的禮物。', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'deDE', 'Stufenaufstieg', 'Lasst ein Haustier auf Stufe 3 aufsteigen.', 'Gut, gut. Es ist wichtig, dass Ihr Eure Haustiere bei guter Gesundheit und kampfbereit haltet!$b$bAls nächstes möchte ich, dass Ihr mehrere Kämpfe bestreitet und ein Haustier auf Stufe 3 bringt. Danach kehrt Ihr zu mir zurück und wir überlegen, wie Ihr Eure eigenen Haustiere fangen könnt!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'esES', '¡Siguiente nivel!', 'Sube a una mascota al nivel 3.', 'Muy bien. Es importante que mantengas a tus mascotas sanas y listas para el combate.$b$bTu siguiente tarea es luchar en unos cuantos duelos y subir a una mascota hasta el nivel 3. Cuando lo consigas, ¡ven a verme y te enseñaré a cazar tus propias mascotas!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'esMX', '¡Siguiente nivel!', 'Sube a una mascota al nivel 3.', 'Muy bien. Es importante que mantengas a tus mascotas sanas y listas para el combate.$b$bTu siguiente tarea es luchar en unos cuantos duelos y subir a una mascota hasta el nivel 3. Cuando lo consigas, ¡ven a verme y te enseñaré a cazar tus propias mascotas!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'frFR', 'On passe au niveau supérieur !', 'Faites atteindre le niveau 3 à une mascotte.', 'Bien, bien. C’est important de garder ses mascottes en bonne santé et prêtes à combattre.$b$bLa prochaine tâche que j’ai à vous confier est de participer à quelques combats de sorte qu\'une de vos mascottes accède au niveau 3. Quand ce sera fait, revenez me voir, nous parlerons de comment attraper vos propres mascottes !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'itIT', 'Nuovo livello!', 'Allena una mascotte fino al livello 3.', 'Bene, bene. È importante tenere le mascotte in buona salute e pronte per uno scontro.$b$bIl prossimo compito che ti assegno è di affrontare un po\' di scontri e portare una mascotte al livello 3. Una volta fatto, torna da me e ti spiegherò come catturare nuove mascotte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'koKR', '레벨 상승!', '애완동물을 3 레벨까지 올려야 합니다.', '좋아요. 아주 잘하셨어요. 애완동물의 건강을 유지해 다음 전투에 대비하는 건 아주 중요하답니다.$b$b제가 드릴 다음 임무는 전투를 몇 번 치러서 애완동물의 레벨을 3까지 올리는 거예요. 이 임무를 마치고 나면 다시 저와 만나요. 그리고 $n 님의 애완동물을 잡는 일에 대해 이야기해봐요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'ptBR', 'Passe de Nível!', 'Elevar uma mascote ao nível 3.', 'Bom, bom. É importante manter suas mascotes saudáveis e prontas pra batalha.$b$bMinha próxima tarefa é: lute algumas vezes e faça uma mascote chegar ao nível 3. Quando você fizer isso, volte aqui e fale comigo sobre capturar suas próprias mascotes!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'ruRU', 'Новый уровень!', 'Повысьте уровень питомца до 3-го.', 'Замечательно. Очень важно следить за здоровьем питомца, чтобы он всегда был готов к битве.$b$bВот мое следующее задание: проведи несколько битв и повысь уровень одного питомца до 3-го. После этого возвращайся ко мне и мы поговорим о том, как научиться ловить питомцев для себя!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'zhCN', '升级！', '将一个宠物提升至3级。', '很好，很好。让宠物保持健康以随时应对战斗，这是非常重要的。$b$b我给你安排的下一个任务是，参加几场对决，将一只宠物升到3级。等你做到之后就回来见我，我会告诉你如何捕捉属于你自己的宠物！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31821, 'zhTW', '升級！', '將一隻寵物提升到等級3。', '很好、很好。讓你的寵物保持健康並準備好面對戰鬥是很種要的。$b$b我給你的下一項任務就是進行幾場戰鬥，將一隻寵物提升到等級3。完成這項任務之後，回來找我討論關於捕捉你自己寵物的事情！', '', '', '', '', '', '', 0);
