INSERT INTO `quest_template_locale` VALUES (31830, 'koKR', '레벨 상승!', '애완동물을 3 레벨까지 올려야 합니다.', '좋소. 아주 잘했소. 애완동물의 건강을 유지해 다음 전투에 대비하는 건 아주 중요하다오.$b$b내가 줄 다음 임무는 전투를 몇 번 치러서 애완동물의 레벨을 3까지 올리는 것이오. 이 임무를 마치고 난 뒤 내게 다시 돌아오면 본인의 애완동물을 잡는 일에 대해 이야기해봅시다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31830, 'ptBR', 'Passe de Nível!', 'Elevar uma mascote ao nível 3.', 'Bom, bom. É importante manter suas mascotes saudáveis e prontas pra batalha.$b$bMinha próxima tarefa é: lute algumas vezes e faça uma mascote chegar ao nível 3. Quando você fizer isso, volte aqui e fale comigo sobre capturar suas próprias mascotes!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31830, 'ruRU', 'Новый уровень!', 'Повысьте уровень питомца до 3-го.', 'Замечательно. Очень важно следить за здоровьем питомца, чтобы он всегда был готов к битве.$b$bВот мое следующее задание: проведи несколько битв и повысь уровень одного питомца до 3-го. После этого возвращайся ко мне и мы поговорим о том, как научиться ловить питомцев для себя!', '', '', '', '', '', '', 0);
