INSERT INTO `quest_template_locale` VALUES (31833, 'deDE', 'Die Ausbildung fortsetzen: Meisterin Woo', 'Sprecht mit Meisterin Woo auf dem Gipfel der Ruhe und schließt Eure Ausbildung mit ihr ab.', 'Willkommen zurück, $n. Die Zeit ist gekommen, den nächsten Abschnitt Eurer Ausbildung zu absolvieren. Sprecht mit Meisterin Woo unten auf dem Ausbildungsgelände, um Eure nächste Lektion zu erhalten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31833, 'esES', 'Prosigue tu entrenamiento: Maestra Woo', 'Habla con la maestra Woo en el Pico de la Serenidad y completa tu entrenamiento.', '$gBienvenido:Bienvenida;, $n, ha llegado el momento de pasar al siguiente nivel de tu entrenamiento. Habla con la maestra Woo en el Campo de Entrenamiento que tenemos abajo para tu próxima lección.', '', '', '', '', '', '', 0);
