INSERT INTO `quest_template_locale` VALUES (31852, 'ptBR', 'Estêvão Lisbane', 'Derrote Estêvão Lisbane numa batalha de mascotes.', 'Você ousa me desafiar de novo? Desta vez, sei que a vitória penderá a meu favor!', '', '', '', 'Que espetáculo!', 'Marcos Gentil', '', 0);
INSERT INTO `quest_template_locale` VALUES (31852, 'ruRU', 'Стивен Лисбен', 'Победите Стивена Лисбена в битве питомцев.', 'Ты осмеливаешься снова бросить мне вызов? На этот раз я выйду победителем в битве!', '', '', '', 'Ну и зрелище!', 'Маркус Йенсен', '', 0);
INSERT INTO `quest_template_locale` VALUES (31852, 'zhCN', '史蒂文·里斯班', '在宠物战斗中击败史蒂文·里斯班。', '你居然还敢来挑战我？我敢说，这次我赢定了！', '', '', '', '无与伦比！', '马库斯·詹森', '', 0);
INSERT INTO `quest_template_locale` VALUES (31852, 'zhTW', '史蒂芬‧李斯本', '在寵物對戰中擊敗史蒂芬‧李斯本。', '你膽敢再次挑戰我？我相信，這一次的戰鬥會是我佔得上風！', '', '', '', '有夠厲害！', '馬庫斯‧傑森', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'deDE', 'Alle Mann an Bord!', 'Sprecht mit General Nazgrim an Bord von Höllschreis Faust.', 'Habt Ihr gehört, $n? Ich muss eine kampferprobte Mannschaft für eine gefährliche Mission zusammenstellen – es gibt Allianzler zu töten.$B$BIhr werdet mich begleiten!$b$bWir kehren nicht eher zurück, als unsere Aufgabe erfüllt ist, also packt Eure Sachen und macht Euch bereit.$b$bIhr findet mich an Bord eines neu in Dienst gestellten Luftschiffs über der Messerbucht.$b$bLasst mich ja nicht warten!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'esES', '¡Todos a bordo!', 'Habla con el general Nazgrim en el Puño de Grito Infernal.', '¿Oyes eso, $n? Hay gente de la Alianza a la que matar y tengo que reunir a un grupo de veteranos.$B$B¡Tú te vienes conmigo!$B$BNo volveremos hasta que hayamos cumplido nuestra misión, así que recoge tus cosas y prepárate.$B$BEstaré a bordo de una nueva nave de guerra sobrevolando la Bahía de Garrafilada.$B$B¡No me hagas esperar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'esMX', '¡Todos a bordo!', 'Habla con el general Nazgrim en el Puño de Grito Infernal.', '¿Oyes eso, $n? Hay gente de la Alianza a la que matar y tengo que reunir a un grupo de veteranos.$B$B¡Tú te vienes conmigo!$B$BNo volveremos hasta que hayamos cumplido nuestra misión, así que recoge tus cosas y prepárate.$B$BEstaré a bordo de una nueva nave de guerra en la Bahía de Garrafilada.$B$B¡No me hagas esperar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'frFR', 'Tous à bord !', 'Parlez au Général Nazgrim à bord du Poing de Hurlenfer.', 'Vous entendez ça, $n ? Il y a de la vermine de l’Alliance à tuer et j\'ai un équipage de grognards à assembler.$b$bVenez avec moi !$b$bOn ne reviendra pas ici tant que nos objectifs ne seront pas remplis, alors faites votre paquetage et tenez-vous $gprêt:prête;.$b$bVous me trouverez à bord d’une nouvelle canonnière qui stationne au-dessus de la baie de Lamepoing.$b$bNe me faites pas attendre !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'itIT', 'Tutti a bordo!', 'Parla con il Generale Nazgrim sulla Furia di Malogrido.', '$n, hai sentito? Ci sono soldati dell\'Alleanza da uccidere e devo radunare un gruppo di veterani.$b$bTu verrai con me!$b$bNon torneremo fin quando la nostra missione non sarà completata, per cui raccogli le tue cose e preparati.$b$bMi troverai a bordo di una nuova cannoniera presso la Baia di Manotagliente.$b$bNon farmi attendere troppo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'koKR', '모두 승선!', '헬스크림의 철권호에 있는 장군 나즈그림과 대화해야 합니다.', '이제 지금 상황은 다 알았겠지, $n? 없애야 할 얼라이언스 놈들이 있으니, 난 최고의 대원들을 소집해야 하네.$b$b자네도 나와 함께 떠날 준비를 하게!$b$b이번 임무를 완수하기 전까지는 돌아올 생각도 할 수 없으니, 필요한 물품을 충분히 챙기는 게 좋겠어.$b$b난 칼날주먹 만의 상공에 머물고 있는 신형 비행포격선에 먼저 승선해 있겠네.$b$b날 오래 기다리게 하지 않는 게 좋을 거야!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'ptBR', 'Todos a bordo!', 'Fale com o General Nazgrim a bordo do Punho de Grito Infernal.', 'Você ouviu, $n? Tem gente da Aliança para matar e eu preciso reunir uma tripulação veterana.$b$bVocê vem comigo!$b$bNós não vamos voltar até completarmos nossa missão, então peguem suas coisas e aprontem-se.$b$bEu estarei a bordo de uma belonave recém-comissionada na Baía Carpunhal.$b$bNão me deixe esperando!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'ruRU', 'Все на борт!', 'Поговорите с генералом Назгримом на борту корабля \"Кулак Адского Крика\".', '$gСлыхал:Слыхала;, $n? Мне нужна команда опытных бойцов для опасного задания.$b$bНужно как следует потрепать Альянс, и ты отправляешься со мной!$b$bМы не вернемся, пока не выполним задачу, так что собирай вещи и готовься.$b$bЯ буду на борту свежеотстроенного боевого судна над бухтой Острорука.$b$bНе заставляй меня ждать!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'zhCN', '全体登船！', '和地狱咆哮之拳上的纳兹戈林将军谈一谈。', '你都听到了吗，$n？那里有很多联盟要杀，我需要召集经验丰富的船员。$b$b你跟我走！$b$b不完成任务我们誓不罢休。赶紧收拾好你的东西，准备出发。$b$b我将登上一艘新近服役的炮艇，就在刃拳海湾那儿。$b$b别让我等太久！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31853, 'zhTW', '全體登船！', '在地獄吼之拳上和納茲格寧姆將軍交談。', '你都聽到了嗎，$n？我得組織一群老練的成員來殺死那些聯盟。$b$b你得和我一起來！$b$b我們直到完成任務才會回來，所以快去整理你的東西，做好準備。$b$b我會在刃拳海灣一艘新服役的戰艦上。$b$b別讓我等太久！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'deDE', 'Analynn', 'Besiegt Analynn in einem Haustierkampf.', 'Ich sehe, Ihr habt mich schon wieder gefunden. Ich nehme an, Ihr möchtet einen weiteren Kampf? So sei es.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'esES', 'Analynn', 'Derrota a Analynn en un duelo de mascotas.', 'Veo que has dado conmigo una vez más. Querrás que nos volvamos a enfrentar, ¿me equivoco? Adelante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'esMX', 'Analynn', 'Derrota a Analynn en un duelo de mascotas.', 'Veo que has dado conmigo una vez más. Querrás que nos volvamos a enfrentar, ¿me equivoco? Adelante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'frFR', 'Analynn', 'Gagnez un combat de mascottes contre Analynn.', 'Je vois que vous m’avez retrouvée. J’imagine que vous souhaitez m’affronter à nouveau ? Alors, c’est d’accord.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'itIT', 'Analynn', 'Sconfiggi Analynn in uno scontro tra mascotte.', 'Vedo che mi hai nuovamente trovata. Presumo tu voglia combattere ancora, vero? Va bene, procediamo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'koKR', '아날린', '애완동물 대전에서 아날린을 꺾어야 합니다.', '다시 한 번 저를 찾아오셨군요. 또 싸워보시려는 건가요? 어디 한번 해보자고요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'ptBR', 'Analynn', 'Vença Analynn numa batalha de mascotes.', 'Então você me encontrou mais uma vez. Suponho que você queira lutar outra vez. Então vamos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'ruRU', 'Аналинн', 'Победите Аналинн в битве питомцев.', 'Вижу, ты снова меня $gнашел:нашла;. Видимо, хочешь снова вызвать меня на бой? Что ж, я не против.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'zhCN', '安纳林', '在宠物战斗中击败安纳林。', '我看到你又来找我了。你还想再打一次，对吧？好，那我们就开始吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31854, 'zhTW', '安納琳', '在寵物對戰中擊敗安納琳。', '看來你再度找到我了。我猜你想要再打一次？好，來吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'deDE', 'Der Gipfel der Ruhe - Setzt Eure Ausbildung fort', 'Reist mithilfe der Zenpilgerfahrt zum Gipfel der Ruhe. Sprecht bei Eurer Ankunft mit Meister Hight am Schrein des Ochsen.', 'Es ist Zeit, dass Ihr zum Gipfel der Ruhe zurückkehrt, um Eure Ausbildung fortzusetzen. Kehrt mithilfe der Zenpilgerfahrt dorthin zurück und sprecht bei Eurer Ankunft mit Meister Hight.', '', '', '', '', '', 'Sprecht mit Meister Hight am Schrein des Ochsen.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'esES', 'El Pico de la Serenidad: Continúa el entrenamiento', 'Usa Peregrinaje zen para viajar al Pico de la Serenidad y habla con el maestro Cenit en el Santuario del Buey cuando llegues.', 'Ha llegado el momento de que vuelvas al Pico de la Serenidad y prosigas con tu entrenamiento. Usa Peregrinaje zen para ir allí y habla con el maestro Cenit cuando llegues.', '', '', '', '', '', 'Habla con: Maestro Cenit. Zona: Santuario del Buey.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'esMX', 'El Pico de la Serenidad: Continúa el entrenamiento', 'Usa Peregrinaje zen para viajar al Pico de la Serenidad y habla con el maestro Cenit en el Santuario del Buey cuando llegues.', 'Ha llegado el momento de que vuelvas al Pico de la Serenidad y prosigas con tu entrenamiento. Usa Peregrinaje zen para ir allí y habla con el maestro Cenit cuando llegues.', '', '', '', '', '', 'Habla con: Maestro Cenit. Zona: Santuario del Buey.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'frFR', 'Le pic de la Sérénité - Poursuivez votre entraînement', 'Utilisez Pèlerinage zen pour vous rendre au Pic de la Sérénité, puis parlez à Maître Culmine au Sanctuaire du Buffle à votre arrivée.', 'Le moment est venu pour vous de retourner au pic de la Sérénité afin de poursuivre votre entraînement. Utilisez Pèlerinage zen pour vous y rendre et parlez à maître Culmine à votre arrivée.', '', '', '', '', '', 'Parlez à Maître Culmine au Sanctuaire du Buffle.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'itIT', 'Picco della Serenità: continuare l\'addestramento', 'Usa il Pellegrinaggio Zen per raggiungere il Picco della Serenità. Quando arrivi, parla con il Maestro Pugno Alto nel Santuario dello Yak.', 'È giunta l\'ora di tornare al Picco della Serenità per continuare il tuo addestramento. Usa il Pellegrinaggio Zen per tornare e parla con il Maestro Pugno Alto.', '', '', '', '', '', 'Parla con il Maestro Pugno Alto nel Santuario dello Yak.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'koKR', '평온의 봉우리 - 훈련은 계속되어야 한다', '순례를 이용해 평온의 봉우리로 가야 합니다. 그곳에 도착하면 흑우의 제단에 있는 사부 하이트와 대화해야 합니다.', '평온의 봉우리로 돌아가 훈련을 계속해야 할 때가 되었습니다. 그곳으로 돌아가려면 순례를 이용하십시오. 평온의 봉우리에 도착하면 사부 하이트와 대화를 나눠야 합니다.', '', '', '', '', '', '흑우의 제단에 있는 사부 하이트와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'ptBR', 'O Pico da Serenidade - Continue seu treinamento', 'Use Peregrinação Zen para ir ao Pico da Serenidade. Fale com Mestre Hight no Templo do Boi quando chegar.', 'Chegou a hora de você voltar ao Pico da Serenidade para continuar seu treinamento. Use a Peregrinação Zen para voltar lá e fale com Mestre Hight quando chegar.', '', '', '', '', '', 'Fale com Mestre Hight no Templo do Boi.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'ruRU', 'Пик Безмятежности - продолжение обучения', 'Воспользуйтесь способностью \"Духовное путешествие\", чтобы отправиться на пик Безмятежности. По прибытии поговорите с мастером Хайтом в Святилище Быка.', 'Пришло время вернуться на пик Безмятежности и продолжить твои тренировки. Воспользуйся способностью \"Духовное путешествие\" и поговори с мастером Хайтом по прибытии.', '', '', '', '', '', 'Поговорите с мастером Хайтом в Святилище Быка.', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'zhCN', '晴日峰：继续训练', '使用禅宗朝圣前往晴日峰，和玄牛神龛的海特大师谈一谈。', '现在，你该回晴日峰继续武僧之道的训练了。你可以使用禅宗朝圣前往那里。抵达后，请去和海特大师谈一谈。', '', '', '', '', '', '去玄牛神龛和海特大师谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (31855, 'zhTW', '寂靜峰 - 繼續你的訓練', '使用禪定歸宗回到寂靜峰。抵達後，到玄牛神殿找高大師。', '回到寂靜峰繼續訓練的時候到了。使用禪定歸宗回到那裡，和高大師談話。', '', '', '', '', '', '到玄牛神殿找高大師。', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'deDE', 'Der Gipfel der Ruhe - Beginnt Eure Ausbildung', 'Reist mithilfe der Zenpilgerfahrt zum Gipfel der Ruhe. Sprecht bei Eurer Ankunft mit Meister Hight am Schrein des Ochsen.', 'Es ist Zeit, dass Ihr zum Gipfel der Ruhe aufbrecht und Euch dort im Weg des Mönchs ausbilden lasst. Begebt Euch mithilfe der Zenpilgerfahrt dorthin und sprecht bei Eurer Ankunft mit Meister Hight.', '', '', '', '', '', 'Sprecht mit Meister Hight am Schrein des Ochsen.', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'esES', 'El Pico de la Serenidad: Comienza el entrenamiento', 'Usa Peregrinaje zen para viajar al Pico de la Serenidad y habla con el maestro Cenit en el Santuario del Buey cuando llegues.', 'Ha llegado el momento de que te adentres en el Pico de la Serenidad e inicies tu entrenamiento en la senda del monje. Usa Peregrinaje zen para ir allí y habla con el maestro Cenit cuando llegues.', '', '', '', '', '', 'Habla con: Maestro Cenit. Zona: Santuario del Buey.', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'esMX', 'El Pico de la Serenidad: Comienza el entrenamiento', 'Usa Peregrinaje zen para viajar al Pico de la Serenidad y habla con el maestro Cenit en el Santuario del Buey cuando llegues.', 'Ha llegado el momento de que te adentres en el Pico de la Serenidad e inicies tu entrenamiento en la senda del monje. Usa Peregrinaje zen para ir allí y habla con el maestro Cenit cuando llegues.', '', '', '', '', '', 'Habla con: Maestro Cenit. Zona: Santuario del Buey.', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'frFR', 'Le pic de la Sérénité - Débutez votre entraînement', 'Utilisez Pèlerinage zen pour vous rendre au Pic de la Sérénité, puis parlez à Maître Culmine au Sanctuaire du Buffle à votre arrivée.', 'Le moment est venu pour vous de partir pour le pic de la Sérénité afin de commencer à apprendre la voie du moine. Utilisez Pèlerinage zen pour vous y rendre et parlez à maître Culmine à votre arrivée.', '', '', '', '', '', 'Parlez à Maître Culmine au Sanctuaire du Buffle.', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'itIT', 'Picco della Serenità: cominciare l\'addestramento', 'Usa il Pellegrinaggio Zen per raggiungere il Picco della Serenità. Quando arrivi, parla con il Maestro Pugno Alto nel Santuario dello Yak.', 'È giunta l\'ora di raggiungere il Picco della Serenità per iniziare il tuo addestramento nella via del Monaco. Usa il Pellegrinaggio Zen per raggiungere il luogo e parla con il Maestro Pugno Alto.', '', '', '', '', '', 'Parla con il Maestro Pugno Alto nel Santuario dello Yak.', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'koKR', '평온의 봉우리 - 훈련이 시작된다', '순례를 이용해 평온의 봉우리로 가야 합니다. 그곳에 도착하면 흑우의 제단에 있는 사부 하이트와 대화해야 합니다.', '평온의 봉우리로 이동해 수도사의 길을 걸어야 할 때가 되었습니다. 그곳으로 돌아가려면 순례를 이용하십시오. 평온의 봉우리에 도착하면 사부 하이트와 대화를 나눠야 합니다.', '', '', '', '', '', '흑우의 제단에 있는 사부 하이트와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (31856, 'ptBR', 'O Pico da Serenidade - Comece seu treinamento', 'Use Peregrinação Zen para ir ao Pico da Serenidade. Fale com Mestre Hight no Templo do Boi quando chegar.', 'Chegou a hora de você voltar ao Pico da Serenidade para continuar seu treinamento de Monge. Use a Peregrinação Zen para voltar lá e fale com Mestre Hight quando chegar.', '', '', '', '', '', 'Fale com Mestre Hight no Templo do Boi.', 0);
