INSERT INTO `quest_template_locale` VALUES (31857, 'ruRU', 'Пик Безмятежности - продолжение обучения', 'Воспользуйтесь способностью \"Духовное путешествие\", чтобы отправиться на пик Безмятежности. По прибытии поговорите с мастером Хайтом в Святилище Быка.', 'Пришло время вернуться на пик Безмятежности и продолжить твои тренировки. Воспользуйся способностью \"Духовное путешествие\" и поговори с мастером Хайтом по прибытии.', '', '', '', '', '', 'Поговорите с мастером Хайтом в Святилище Быка.', 0);
