INSERT INTO `quest_template_locale` VALUES (31861, 'itIT', 'Picco della Serenità: continuare l\'addestramento', 'Usa il Pellegrinaggio Zen per raggiungere il Picco della Serenità. Quando arrivi, parla con il Maestro Pugno Alto nel Santuario dello Yak.', 'È giunta l\'ora di tornare al Picco della Serenità per continuare il tuo addestramento. Usa il Pellegrinaggio Zen per tornare e parla con il Maestro Pugno Alto.', '', '', '', '', '', 'Parla con il Maestro Pugno Alto nel Santuario dello Yak.', 0);
INSERT INTO `quest_template_locale` VALUES (31861, 'koKR', '평온의 봉우리 - 훈련은 계속되어야 한다', '순례를 이용해 평온의 봉우리로 가야 합니다. 그곳에 도착하면 흑우의 제단에 있는 사부 하이트와 대화해야 합니다.', '평온의 봉우리로 돌아가 훈련을 계속해야 할 때가 되었습니다. 그곳으로 돌아가려면 순례를 이용하십시오. 평온의 봉우리에 도착하면 사부 하이트와 대화를 나눠야 합니다.', '', '', '', '', '', '흑우의 제단에 있는 사부 하이트와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (31861, 'ptBR', 'O Pico da Serenidade - Continue seu treinamento', 'Use Peregrinação Zen para ir ao Pico da Serenidade. Fale com Mestre Hight no Templo do Boi quando chegar.', 'Chegou a hora de você voltar ao Pico da Serenidade para continuar seu treinamento. Use a Peregrinação Zen para voltar lá e fale com Mestre Hight quando chegar.', '', '', '', '', '', 'Fale com Mestre Hight no Templo do Boi.', 0);
INSERT INTO `quest_template_locale` VALUES (31861, 'ruRU', 'Пик Безмятежности - продолжение обучения', 'Воспользуйтесь способностью \"Духовное путешествие\", чтобы отправиться на пик Безмятежности. По прибытии поговорите с мастером Хайтом в Святилище Быка.', 'Пришло время вернуться на пик Безмятежности и продолжить твои тренировки. Воспользуйся способностью \"Духовное путешествие\" и поговори с мастером Хайтом по прибытии.', '', '', '', '', '', 'Поговорите с мастером Хайтом в Святилище Быка.', 0);
INSERT INTO `quest_template_locale` VALUES (31861, 'zhCN', '晴日峰：继续训练', '使用禅宗朝圣前往晴日峰，和玄牛神龛的海特大师谈一谈。', '现在，你该回晴日峰继续武僧之道的训练了。你可以使用禅宗朝圣前往那里。抵达后，请去和海特大师谈一谈。', '', '', '', '', '', '去玄牛神龛和海特大师谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (31861, 'zhTW', '寂靜峰 - 繼續你的訓練', '使用禪定歸宗回到寂靜峰。抵達後，到玄牛神殿找高大師。', '回到寂靜峰繼續訓練的時候到了。使用禪定歸宗回到那裡，和高大師談話。', '', '', '', '', '', '到玄牛神殿找高大師。', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'deDE', 'Zonya die Sadistin', 'Besiegt Zonya die Sadistin in einem Haustierkampf.', 'Die Beute läuft in das Revier des Raubtiers, ohne sich der Gefahr bewusst zu sein, in der sie sich befindet. Diesmal werdet Ihr Zonya keine Schwierigkeiten bereiten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'esES', 'Zonya la Sádica', 'Derrota a Zonya la Sádica en un duelo de mascotas.', 'La presa se dirige al territorio del depredador, ajena al peligro que corre. Esta vez no serás un problema para Zonya.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'esMX', 'Zonya la Sádica', 'Derrota a Zonya la Sádica en un duelo de mascotas.', 'La presa se dirige al territorio del depredador, ajena al peligro que corre. Esta vez no serás un problema para Zonya.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'frFR', 'Zonya la Sadique', 'Gagnez un combat de mascottes contre Zonya la Sadique.', 'La proie, elle s’aventure sur le territoire du prédateur sans se rendre compte du danger qu’elle court. Cette fois, vous poserez pas de problèmes à Zonya.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'itIT', 'Zonya la Sadica', 'Sconfiggi Zonya la Sadica in uno scontro tra mascotte.', 'La preda cammina nel territorio del predatore, senza sapere in che pericolo sta incappando. Chesta volta nun sarai nu problema per Zonya.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'koKR', '성격 나쁜 존야', '애완동물 대전에서 성격 나쁜 존야를 꺾어야 합니다.', '어떤 위험이 있는지도 모르고 먹잇감이 포식자의 입속으로 걸어오시는군요. 이 존야한테는 손가락 하나 까딱 못할 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'ptBR', 'Zonya, a Sádica', 'Derrote Zonya, a Sádica, numa batalha de mascotes.', 'A presa tá indo pro território do predador, sem saber do perigo que corre. Dessa vez cê num vai causar problema pra Zonya.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'ruRU', 'Зоня Садистка', 'Победите Зоню Садистку в битве питомцев.', 'Добыча сама идет в лапы хищника, даже не подозревая об опасности. На этот раз Зоня с тобой позабавится.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31862, 'zhCN', '嗜虐的索亚', '在宠物战斗中击败嗜虐的索亚。', '啊，猎物闯进掠食者的地盘了，完全不知道自己处于危险之中。你这回休想再给索亚添乱了。', '', '', '', '', '', '', 0);
