INSERT INTO `quest_template_locale` VALUES (31871, 'frFR', 'Gluk le traître', 'Gagnez un combat de mascottes contre Gluk le traître.', 'Vous avez déjà battu Gluk, mais cette fois, Gluk va réduire en purée $gle:la:r; minuscule $R !', '', '', '', '', '', '', 0);
