INSERT INTO `quest_template_locale` VALUES (31871, 'koKR', '배신자 글루크', '애완동물 대전에서 배신자 글루크를 꺾어야 합니다.', '너 옛날에 나 이긴 적 있지만, 이번에는 글루크가 한 방 먹인다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31871, 'ptBR', 'Traidor Gluk', 'Vença o Traidor Gluk numa batalha de mascotes.', 'Você derrotar Gluk antes, mas agora Gluk arrebenta com $r fracote!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31871, 'ruRU', 'Предатель Глак', 'Победите предателя Глака в битве питомцев.', 'Ты победить Глака до этого, но сегодня Глак открутить голову $gничтожному:ничтожному:r; |3-2($r).', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31871, 'zhCN', '叛徒格鲁克', '在宠物战斗中击败叛徒格鲁克。', '你打败过格鲁克，可这次格鲁克会扫平你这废物$r！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31871, 'zhTW', '背叛者格魯克', '在寵物對戰中擊敗背叛者格魯克。', '你擊敗過格魯克，但現在格魯克要打爛小$r！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31872, 'deDE', 'Merda Starkhuf', 'Besiegt Merda Starkhuf in einem Haustierkampf.', 'Genau wie das Land, das immer weiterwächst, ist auch mein Team stärker als je zuvor. Seid Ihr sicher, dass Ihr gegen mich antreten möchtet? Wenn Ihr Euch jetzt geschlagen gebt, ist das keine Schande.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31872, 'esES', 'Herda Pezuña Fuerte', 'Derrota a Herda Pezuña Fuerte en un duelo de mascotas.', 'La tierra no deja de dar sus frutos y mi equipo no deja de ganar fuerza. ¿Seguro que quieres enfrentarte a mí? No hay vergüenza en admitir tu derrota ahora.', '', '', '', '', '', '', 0);
