INSERT INTO `quest_template_locale` VALUES (31876, 'frFR', 'Les maîtres des encres de l’Arboretum', 'Parlez au Maître des encres Wei à l’Arboretum, dans la Forêt de Jade.', 'J’ai entendu dire que les maîtres des encres de l’Arboretum avaient désespérément besoin de l’aide d’$gun:une; calligraphe de grand talent. Si vous souhaitez leur apporter votre aide, allez parler au maître des encres Wei.', '', '', '', '', '', 'Parlez au Maître des encres Wei à l’Arboretum, dans la Forêt de Jade.', 0);
INSERT INTO `quest_template_locale` VALUES (31876, 'itIT', 'I Mastri Scriba dell\'Arboreto', 'Parla con il Mastro Scriba Wei all\'Arboreto nella Foresta di Giada.', 'Ho sentito che i Mastri Scriba dell\'Arboreto stanno cercando l\'aiuto di un esperto runografo. Se sei $ginteressato:interessata;, parla con il Mastro Scriba Wei.', '', '', '', '', '', 'Parla con il Mastro Scriba Wei all\'Arboreto nella Foresta di Giada.', 0);
INSERT INTO `quest_template_locale` VALUES (31876, 'koKR', '도원의 잉크장인', '비취 숲의 도원에 있는 잉크장인 웨이와 대화해야 합니다.', '저기 도원에 있는 잉크장인들이 솜씨 좋은 주문각인사를 찾는다고 하네. 혹시 자네가 도와줄 수 있다면, 가서 잉크장인 웨이와 말을 해보게.', '', '', '', '', '', '비취 숲의 도원에 있는 잉크장인 웨이와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (31876, 'ptBR', 'Os Mestres Escribas do Arboreto', 'Fale com o Mestre Escriba Wei no Arboreto, na Floresta de Jade.', 'Ouvi dizer que os Mestres Escribas do Arboreto estão precisando de um escriba de grande perícia. Por que você não vai falar com o Mestre Escriba Wei, no Arboreto, que fica na Floresta de Jade?', '', '', '', '', '', 'Fale com o Mestre Escriba Wei no Arboreto, na Floresta de Jade.', 0);
INSERT INTO `quest_template_locale` VALUES (31876, 'ruRU', 'Мастера каллиграфии Дендрария', 'Поговорите с мастером каллиграфии Вэем в Дендрарии, что в Нефритовом лесу.', 'Мне говорили, что мастерам каллиграфии в Дендрарии требуется помощь умелого начертателя. Если хочешь помочь, поговори с мастером каллиграфии Вэем.', '', '', '', '', '', 'Поговорите с мастером каллиграфии Вэем в Дендрарии, что в Нефритовом лесу.', 0);
