INSERT INTO `quest_template_locale` VALUES (31905, 'frFR', 'Grazzle le Grand', 'Gagnez un combat de mascottes contre Grazzle le Grand.', 'Encore vous ?! Vous cherchez vraiment à vous faire dévorer par un dragon noir, n’est-ce pas ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31905, 'itIT', 'Grazzle il Grande', 'Sconfiggi Grazzle il Grande in uno scontro tra mascotte.', 'Sei già di ritorno?!? A quanto sembra vuoi proprio essere $gmangiato:mangiata; da un drago nero!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31905, 'koKR', '위대한 그라즐', '애완동물 대전에서 위대한 그라즐을 꺾어야 합니다.', '돌아온 건가? 검은용한테 먹히러 왔군!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31905, 'ptBR', 'Grazzle, o Grande', 'Vença Grazzle, o Grande, numa batalha de mascotes.', 'Você voltou?! Está implorando pra ser devorad$go:a; por um dragão negro!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31905, 'ruRU', 'Греззл Великий', 'Победите Греззла Великого в битве питомцев.', 'Это снова ты?! Да ты просто напрашиваешься на обед черному дракону!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31905, 'zhCN', '杰出的高雷兹勒', '在一场宠物战斗中击败杰出的高雷兹勒。', '你回来了？！真是自寻死路，准备好被黑龙一口吞下去吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31905, 'zhTW', '『強者』葛瑞森', '在寵物對戰中擊敗『強者』葛瑞森。', '你回來了？你是拿自己來餵黑龍的！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'deDE', 'Kela Grimmtotem', 'Besiegt Kela Grimmtotem in einem Haustierkampf.', 'Ihr besitzt die Kühnheit, ein Mal mehr Fuß ins Land derer von Grimmtotem zu setzen, um mich herauszufordern? Ihr werdet Euren Entschluss bereuen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'esES', 'Kela Tótem Siniestro', 'Derrota a Kela Tótem Siniestro en un duelo de mascotas.', '¿Tienes el valor para adentrarte de nuevo en el corazón de las tierras Tótem Siniestro para desafiarme? ¡Te arrepentirás!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'esMX', 'Kela Tótem Siniestro', 'Derrota a Kela Tótem Siniestro en un duelo de mascotas.', '¿Tienes el valor para adentrarte de nuevo en el corazón de las tierras Tótem Siniestro para desafiarme? ¡Te arrepentirás!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'frFR', 'Kela Totem-Sinistre', 'Gagnez un combat de mascottes contre Kela Totem-Sinistre.', 'Vous avez l’audace de vous risquer à nouveau en plein cœur d’un territoire totem-sinistre pour m’affronter ? Vous allez regretter votre décision !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'itIT', 'Kela Totem Truce', 'Sconfiggi Kela Totem Truce in uno scontro tra mascotte.', 'Sei così $gcoraggioso:coraggiosa; da avventurarti nuovamente nel cuore delle terre dei Totem Truce per sfidarmi? Te ne pentirai!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'koKR', '켈라 그림토템', '애완동물 대전에서 켈라 그림토템을 꺾어야 합니다.', '그림토템의 땅 한가운데까지 와서 제게 다시 도전하다니 꽤 용감하군요! 하지만 곧 후회하게 만들어줄 거예요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'ptBR', 'Kela Temível Totem', 'Vença Kela Temível Totem numa batalha de mascotes.', 'Você ousa se aventurar de novo nas terras dos Temível Totem para me desafiar? Você se arrependerá de sua decisão!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'ruRU', 'Кела Зловещий Тотем', 'Победите Келу Зловещий Тотем в битве питомцев.', 'Как опрометчиво с твоей стороны было снова вернуться в земли племени Зловещего Тотема, чтобы вновь вызвать меня на бой. Ты пожалеешь о своей наглости!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'zhCN', '科拉·恐怖图腾', '在一场宠物战斗中击败科拉·恐怖图腾。', '你竟然胆子大到重新回到恐怖图腾的心脏地带来挑战我？我会让你后悔做出这个决定的！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31906, 'zhTW', '凱拉‧恐怖圖騰', '在寵物對戰中擊敗凱拉‧恐怖圖騰。', '你好大膽子，竟敢來到恐怖圖騰的領土中心，再次和我挑戰？你會後悔你的決定！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'deDE', 'Zoltan', 'Besiegt Zoltan in einem Haustierkampf.', 'Ich werde von Tag zu Tag stärker. Widerstand ist zwecklos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'esES', 'Zoltan', 'Derrota a Zoltan en un duelo de mascotas.', 'Mi poder crece cada día que pasa. La resistencia es inútil.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'esMX', 'Zoltan', 'Derrota a Zoltan en un duelo de mascotas.', 'Mi poder crece cada día que pasa. La resistencia es inútil.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'frFR', 'Zoltan', 'Gagnez un combat de mascottes contre Zoltan.', 'Chaque jour qui passe voit ma puissance grandir. Toute résistance serait vaine.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'itIT', 'Zoltan', 'Sconfiggi Zoltan in uno scontro tra mascotte.', 'Ogni giorno che passa, il mio potere aumenta. Resistere è inutile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'koKR', '졸탄', '애완동물 대전에서 졸탄을 꺾어야 합니다.', '매일매일 내 힘은 강력해진다오. 저항해도 소용없소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'ptBR', 'Zoltan', 'Derrote Zoltan em uma batalha de mascotes.', 'Meu poder cresce a cada dia que passa. A resistência é fútil.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'ruRU', 'Золтан', 'Победите Золтана в битве питомцев.', 'С каждым днем моя сила растет. Сопротивление бессмысленно.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'zhCN', '佐尔坦', '在一场宠物战斗中击败佐尔坦。', '我的力量与日俱增。反抗是毫无用处的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31907, 'zhTW', '周坦', '在寵物對戰中擊敗周坦。', '我的力量一天比一天更強。抵抗是無用的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'deDE', 'Elena Flatterflug', 'Besiegt Elena Flatterflug in einem Haustierkampf.', 'Ich sehe schon, Ihr seid bereit für ein weiteres Kräftemessen in den Lüften. Fliegen wir los!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'esES', 'Elena Alabatiente', 'Derrota a Elena Alabatiente en un duelo de mascotas.', 'Veo que estás $glisto:lista; para otro encuentro en el cielo. ¡Alcemos el vuelo!', '', '', '', '', '', '', 0);
