INSERT INTO `quest_template_locale` VALUES (31908, 'frFR', 'Elena Voltemouche', 'Gagnez un combat de mascottes contre Elena Voltemouche.', 'Je vois que vous êtes $gprêt:prête; pour un nouveau match dans les airs. Envolons-nous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'itIT', 'Elena Fluttuavolo', 'Sconfiggi Elena Fluttuavolo in uno scontro tra mascotte.', 'Vedo che vuoi farti sconfiggere nuovamente. Fatti sotto!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'koKR', '엘레나 플러터플라이', '애완동물 대전에서 엘레나 플러터플라이를 꺾어야 합니다.', '하늘에서 당신이 또 다른 대결을 준비하는 걸 봤어요. 자, 한 번 날아볼까요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'ptBR', 'Helena Asaleve', 'Vença Helena Asaleve numa batalha de mascotes.', 'Vejo que você está $gpronto:pronta; para outra luta nos céus. Vamos lá!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'ruRU', 'Елена Порхающая Бабочка', 'Победите Елену Порхающую Бабочку в битве питомцев.', 'Вижу, ты $gподготовился:подготовился; к очередной воздушной схватке. Ну что, полетели?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'zhCN', '艾琳娜·振翼', '在一场宠物战斗中击败艾琳娜·振翼。', '看来你准备好在空中和我再战一回合了。让我们开始飞翔吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31908, 'zhTW', '艾蓮娜‧振翼', '在寵物對戰中擊敗艾蓮娜‧振翼。', '看來你還想再被打飛。那就飛吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31909, 'deDE', 'Großmeisterin Trixxy', 'Besiegt die eiskalte Trixxy in einem Haustierkampf.', 'Halt! Eure letzte Begegnung hat Euch wohl endlich aufgewärmt, was? Das soll sich ändern.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31909, 'esES', 'Gran maestra Trixxy', 'Derrota a Trixxy Sangrefría en un duelo de mascotas.', '¡Vaya! Ya has entrado en calor después de nuestro último encuentro, ¿verdad? Pues eso va a cambiar.', '', '', '', '', '', '', 0);
