INSERT INTO `quest_template_locale` VALUES (31909, 'frFR', 'Le grand maître Trixxy', 'Gagnez un combat de mascottes contre Trixxy l’Impassible.', 'Par la glace ! Vous avez fini par vous réchauffer depuis notre dernière rencontre, n’est-ce pas ? On va remédier à cela.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31909, 'itIT', 'Gran Maestra Trixxy', 'Sconfiggi Trixxy la Gelida in uno scontro tra mascotte.', 'Ti sei $gscaldato:scaldata; un po\' dal nostro ultimo incontro? Vediamo di porre rimedio al problema.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31909, 'koKR', '특급 조련사 트릭시', '애완동물 대전에서 차디찬 트릭시를 꺾어야 합니다.', '꼼짝 마요! 이제서야 지난 번 대결 이후로 준비 운동을 다 마친 거, 맞죠? 그럼 어디 시험해보자고요.', '', '', '', '', '', '', 0);
