INSERT INTO `quest_template_locale` VALUES (31910, 'koKR', '데이비드 코세', '애완동물 대전에서 데이비드 코세를 꺾어야 합니다.', '연구를 거쳐 내 훈련 체계가 크게 나아졌네. 이번엔 꼭 이기고 말 걸세.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31910, 'ptBR', 'Davi Kosse', 'Vença Davi Kosse numa batalha de mascotes.', 'Minha pesquisa ajudou muito meu esquema de treinamento. Eu derrotarei você dessa vez.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31910, 'ruRU', 'Дэвид Косс', 'Победите Дэвида Косса в битве питомцев.', 'Мои исследования позволили значительно увеличить потенциал моей команды. Теперь мне обеспечена победа в бою.', '', '', '', '', '', '', 0);
