INSERT INTO `quest_template_locale` VALUES (31911, 'deDE', 'Deiza Seuchenhorn', 'Besiegt Deiza Seuchenhorn in einem Haustierkampf.', 'Noch habt Ihr den wahren Pfad der Untoten nicht eingeschlagen. Vielleicht wird Euch ein weiterer Kampf die Richtung weisen.', '', '', '', '', '', '', 0);
