INSERT INTO `quest_template_locale` VALUES (31911, 'koKR', '데이자 플레이그혼', '애완동물 대전에서 데이자 플레이그혼을 꺾어야 합니다.', '진정한 불사의 길을 밟아보지 못했군요. 한 번 더 싸워보면 그 길을 보게 될 거예요.', '', '', '', '', '', '', 0);
