INSERT INTO `quest_template_locale` VALUES (31911, 'ruRU', 'Дэйза Плейгхорн', 'Победите Дэйзу Плейгхорн в битве питомцев.', 'Ты еще не $gсвернул:свернула; на истинный путь – путь нежити. Возможно, этот бой выведет тебя на верную дорогу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31911, 'zhCN', '德扎·疫角', '在一场宠物战斗中击败德扎·疫角。', '你还未真正踏上亡灵之道。或许另一场战斗能让你开窍。', '', '', '', '', '', '', 0);
