INSERT INTO `quest_template_locale` VALUES (31912, 'deDE', 'Kortas Düsterhammer', 'Besiegt Kortas Düsterhammer in einem Haustierkampf.', 'Ich habe gehört, dass Ihr gegen meinen Bruder, diesen Narren, gekämpft habt. Er hat wohl verloren, was?$B$BDann darf ich wohl jetzt zeigen, was ich draufhabe!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31912, 'esES', 'Kortas Martilloscuro', 'Derrota a Kortas Martilloscuro en un duelo de mascotas.', 'He oído que te enfrentaste al tonto de mi hermano. Perdió, ¿eh?$B$B¡Parece que ha llegado mi momento!', '', '', '', '', '', '', 0);
