INSERT INTO `quest_template_locale` VALUES (31912, 'frFR', 'Kortas Sombremartel', 'Gagnez un combat de mascottes contre Kortas Sombremartel.', 'J’ai entendu dire que vous aviez affronté mon idiot de frangin. Il a perdu, pas vrai ?$B$BOn dirait que mon jour de gloire est arrivé !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31912, 'itIT', 'Kortas Martelnero', 'Sconfiggi Kortas Martelnero in uno scontro tra mascotte.', 'Ho sentito che hai combattuto contro quel folle di mio fratello. Ha perso vero?$B$BOra toccherà a me essere il tuo avversario e sconfiggerti!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31912, 'koKR', '코르타스 다크해머', '애완동물 대전에서 코르타스 다크해머를 꺾어야 합니다.', '바보 같은 우리 형과 싸워서 이겼다더군?$B$B지금은 내가 빛날 차례인 것 같은데!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31912, 'ptBR', 'Kortas Trevamalho', 'Vença Kortas Trevamalho numa batalha de mascotes.', 'Ouvi dizer que você lutou com o tolo do meu irmão. Ele perdeu, é?$B$BParece que é minha hora de brilhar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31912, 'ruRU', 'Кортан Черномолот', 'Победите Кортана Черномолота в битве питомцев.', 'Слышал, что ты победил моего туповатого братца. Это так?$B$BЗначит, настал час моего триумфа!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31912, 'zhCN', '科塔兹·黑锤', '在宠物战斗中击败科塔兹·黑锤。', '我听说我那个跟蠢猪一样的兄弟和你打了一场。还输了，是不？$B$B看来是到我该露两手的时候了！', '', '', '', '', '', '', 0);
