INSERT INTO `quest_template_locale` VALUES (31913, 'deDE', 'Everessa', 'Besiegt Everessa in einem Haustierkampf.', 'Ich glaube, ich komme langsam hinter die Geheimnisse dieses Orts... Bald schon werde ich nicht mehr aufzuhalten sein!', '', '', '', '', '', '', 0);
