INSERT INTO `quest_template_locale` VALUES (31914, 'deDE', 'Durin Düsterhammer', 'Besiegt Durin Düsterhammer in einem Haustierkampf.', 'Seit meiner Zeit im Dienst des Feuerlords bin ich für alles gewappnet. Nichts, was Ihr mir entgegenschleudert, könnte mich erschüttern!', '', '', '', '', '', '', 0);
