INSERT INTO `quest_template_locale` VALUES (31914, 'frFR', 'Durin Sombremartel', 'Gagnez un combat de mascottes contre Durin Sombremartel.', 'L’époque où je servais le seigneur du Feu m’a préparé à tout. Vos gesticulations n’arriveront pas à me faire peur !', '', '', '', '', '', '', 0);
