INSERT INTO `quest_template_locale` VALUES (31917, 'deDE', 'Heimkehr des Zähmers', 'Meldet Euch bei Marlene Trichdie in Sturmwind.', 'Ihr könnt Euch jetzt kopfüber in waschechte Haustierkämpfe stürz\'n. Kehrt zu Eurer Trainerin Marlene Trichdie nach Sturmwind zurück und macht Euch für das Abenteuer Eures Lebens bereit!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'esES', 'La bienvenida de un domador', 'Ve a ver a Audrey Burnhep en Ventormenta.', 'Tash $glishto:lishta; pa entral en la primera divishión de losh duelosh de mashcotas. Vuelve con tu in’tructora, la señorita Burnhep, a Ventormenta, ¡y prepálate pa\' l’aventura de tu vida, broder!', '', '', '', '', '', '', 0);
