INSERT INTO `quest_template_locale` VALUES (31917, 'frFR', 'Le dresseur est de retour', 'Allez vous présenter à Audrey Burnhep à Hurlevent.', 'Vous êtes fin $gprêt:prête; à intégrer le circuit officiel des combats de mascottes, camarade. Retournez voir mam’zelle Burnhep, à Hurlevent, et préparez-vous pour l’aventure de votre vie !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'itIT', 'Il benvenuto per un allenatore', 'Fai rapporto a Audrey Burnhep a Roccavento.', 'Shei $gpronto:pronta; a entrare nel vivo degli shcontri tra mascotte. Torna dalla tua ishtruttrice, Audrey Burnhep a Roccavento, e preparati all\'avventura della tua vita!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'koKR', '조련사의 귀환', '스톰윈드에 있는 오드리 번헵에게 보고해야 합니다.', '자넨 진정한 애완동물 대전의 세계에 들어갈 준비가 됐군. 자네 조련사인 스톰윈드에 있는 번헵 양에게 돌아가서 일생일대의 모험을 준비하게나!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'ptBR', 'O treinador volta pra casa', 'Apresente-se a Audreia Répibãrne em Ventobravo.', 'Você está $gpronto:pronta; para o circuito principal de batalhas de mascotes, chapinha. Volte a falar com sua instrutora, a Dona Répibãrne em Ventobravo, e prepare-se para uma aventura sensacional!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'ruRU', 'Возвращение укротителя', 'Обратитесь к Одри Бернхеп в Штормграде.', '$gГотов:Готова; начать полный цикл битв питомцев, $gдруг:подруга;? Тогда вернись в Штормград к своему тренеру Одри Бернхеп и готовься к великому приключению!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'zhCN', '返乡的驯宠师', '去暴风城找奥黛丽·伯恩赫普。', '你是时候进入宠物战斗的最高殿堂了，伙计。回到你在暴风城的训练师，伯恩赫普小姐那里。准备好迎接你人生最刺激的一次冒险吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31917, 'zhTW', '訓練師再會', '到暴風城找奧德蕾‧燃憂。', '老兄，你已經很熟悉寵物戰鬥了。回去暴風城找你的訓練師，奧德雷‧燃憂，準備開始你這輩子的大冒險！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'deDE', 'Heimkehr des Zähmers', 'Meldet Euch bei Varzok in Orgrimmar.', 'Wenn Ihr mich schlagen könnt, dann seid Ihr bereit für die ganz große Bühne! Sprecht mit Varzok in Orgrimmar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'esES', 'La bienvenida de un domador', 'Ve a ver a Varzok en Orgrimmar.', '¡Si has logrado derrotarme es que estás $glisto:lista; para subir de liga! ¡Ve a hablar con Varzok en Orgrimmar!', '', '', '', '', '', '', 0);
