INSERT INTO `quest_template_locale` VALUES (31918, 'frFR', 'Le dresseur est de retour', 'Présentez-vous à Varzok à Orgrimmar.', 'Si vous battre Gluk alors vous $gprêt:prête; pour gros combat ! Aller parler à Varzok dans Orgrimmar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'itIT', 'Il benvenuto per un allenatore', 'Fai rapporto a Varzok a Orgrimmar.', 'Se riesci a battermi, significa che sei $gpronto:pronta; per sfide maggiori! Parla con Varzok a Orgrimmar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'koKR', '조련사의 귀환', '오그리마에 있는 바르조크에게 보고해야 합니다.', '저를 이겼다면 더 큰 전투를 치를 준비가 됐다는 거예요! 오그리마에 있는 바르조크에게 이야기해보세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'ptBR', 'O treinador volta pra casa', 'Apresente-se a Varzok em Orgrimmar.', 'Se cê conseguiu me vencer, então já deve tá $gpronto:pronta; pra brilhar! Vai falar com Varzok em Orgrimmar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'ruRU', 'Возвращение укротителя', 'Обратитесь к Варзоку в Оргриммаре.', 'Если ты сумеешь одержать надо мной верх, тебя ждет большая слава! Отправляйся в Оргриммар и поговори с Варзоком!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'zhCN', '返乡的驯宠师', '去奥格瑞玛找瓦佐克。', '如果你打败了我，那就准备迎接属于你自己的伟大时刻吧！去奥格瑞玛与瓦佐克谈谈。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31918, 'zhTW', '訓練師再會', '到奧格瑪找伐索克。', '如果你擊敗我，那你已經大大準備好啦！回去找奧格瑪的伐索克吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'deDE', 'Kampfhaustierzähmer: Scherbenwelt', 'Besiegt Nicki Mikrotech, Ras\'an, Narrok und Morulu den Älteren in Haustierkämpfen.', 'Ihr habt die Meisterzähmer Kalimdors und der Östlichen Königreiche bezwungen, doch Eure nächste Herausforderung wird Euch auf eine sehr viel härtere Probe stellen.$B$BIn der Scherbenwelt gibt es vier Zähmer, die Ihr jetzt besiegen müsst: Nicki Mikrotech auf der Höllenfeuerhalbinsel, Ras\'an in den Zangarmarschen, Narrok in Nagrand und Morulu den Älteren in Shattrath.$B$BBesiegt sie, wenn Ihr den Pfad zu wahrer Größe beschreiten wollt.', '', 'Ihr schafft es schon!', 'Marlene Trichdie', 'Ich habt Euch wirklich gemausert!', 'Marlene Trichdie', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'esES', 'Domadores de mascotas de duelo: Terrallende', 'Derrota a Nicki Pocatécnica, a Ras\'an, a Narrok y a Morulu el Ancestro en un duelo de mascotas.', 'Has superado a los maestros domadores de Kalimdor y los Reinos del Este, pero tu próximo desafío será mucho más difícil.$B$BAhora tendrás que derrotar a cuatro domadores en Terrallende: Nicky Pocatécnica, en la Península del Fuego Infernal; Ras\'an en la Marisma de Zangar; Narrok, en Nagrand y a Morulu el Ancestro, en Shattrath.$B$BDebes vencerlos si quieres avanzar en tu camino hacia la grandeza.', '', '¡Tú puedes!', 'Audrey Burnhep', '¡Esto se te da muy bien!', 'Audrey Burnhep', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'esMX', 'Domadores de mascotas de duelo: Terrallende', 'Derrota a Nicki Pocatécnica, a Ras\'an, a Narrok y a Morulu el Ancestro en un duelo de mascotas.', 'Has superado a los maestros domadores de Kalimdor y los Reinos del Este, pero tu próximo desafío será mucho más difícil.$B$BAhora tendrás que derrotar a cuatro domadores en Terrallende: Nicky Pocatécnica, en la Península del Fuego Infernal; Ras\'an en la Marisma de Zangar; Narrok, en Nagrand y a Morulu el Ancestro, en Shattrath.$B$BDebes vencerlos si quieres avanzar en tu camino hacia la grandeza.', '', '¡Tú puedes!', 'Audrey Burnhep', '¡Esto se te da muy bien!', 'Audrey Burnhep', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'frFR', 'Dompteurs de mascottes de combat : l’Outreterre', 'Gagnez un combat de mascottes contre Nicky Minitech, Ras’an, Narrok et Morulu l’Ancien.', 'Vous avez fait mordre la poussière aux maîtres dresseurs de Kalimdor et des royaumes de l’Est, mais votre prochain défi sera bien plus difficile.$b$bVous devez maintenant affronter les quatre dompteurs de l’Outreterre : Nicky Minitech dans la péninsule des Flammes infernales, Ras’an dans le marécage de Zangar, Narrok à Nagrand et Morulu l\'Ancien à Shattrath.$b$bVous devez les vaincre pour poursuivre votre chemin sur les routes de la grandeur.', '', 'Vous pouvez le faire !', 'Audrey Burnhep', 'Vous vous en sortez vraiment bien !', 'Audrey Burnhep', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'itIT', 'Allenatori di mascotte: Terre Esterne', 'Sconfiggi Nicki Microtecno, Ras\'an, Narrok e Morulu l\'Anziano in degli scontri tra mascotte.', 'Hai battuto i Maestri Allenatori di Kalimdor e dei Regni Orientali, ma la prossima sfida sarà molto più ardua.$B$BDovrai sconfiggere quattro allenatori nelle Terre Esterne: Nicki Microtecno nella Penisola del Fuoco Infernale, Ras\'an in Zangarmarsh, Narrok in Nagrand e Morulu l\'Anziano in Shattrath.$B$BDevi avere la meglio se vuoi procedere sul percorso della grandezza.', '', 'Ce la puoi fare!', 'Audrey Burnhep', 'Sai proprio come fare!', 'Audrey Burnhep', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'koKR', '전투 애완동물 조련사: 아웃랜드', '애완동물 대전에서 니키 타이니테크, 라스안, 나로크, 어르신 모룰루를 꺾어야 합니다.', '칼림도어와 동부 왕국의 뛰어난 조련사들은 모두 꺾었지만, 아직 다음 도전 상대들이 남았어요.$B$B아웃랜드에는 당신이 꺾어야 할 네 명의 조련사가 있답니다. 지옥불 반도의 니키 타이니테크, 장가르 습지대의 라스안, 나그란드의 나로크, 샤트라스의 어르신 모룰루를 찾아가세요.$B$B위대함의 여정을 계속 가려면 그들을 꺾어야 해요.', '', '당신은 할 수 있어요!', '오드리 번헵', '정말 실력이 부쩍부쩍 느는군요!', '오드리 번헵', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'ptBR', 'Domadores de Batalha: Terralém', 'Derrote Nicky Minitreco, Ras’an, Narrok e Morulu, o Ancião, numa batalha de mascotes.', 'Você derrotou os melhores domadores de Kalimdor e dos Reinos do Leste, mas seu próximo desafio será bem mais difícil.$B$BHá quatro domadores em Terralém que você precisa derrotar: Nicky Minitreco na Península Fogo do Inferno, Ras\'an no Pântano Zíngaro, Narrok em Nagrand e Morulu, o Ancião, em Shattrath.$B$BVocê precisa derrotá-los para continuar a caminho da grandeza.', '', 'Você consegue!', 'Audreia Répibãrne', 'Você está indo muito bem!', 'Audreia Répibãrne', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'ruRU', 'Укротители боевых питомцев: Запределье', 'Победите Ники Мелкотех, Рас\'ана, Наррока и Морулу Древнего в битвах питомцев.', 'Тебе удалось одержать верх над самыми опытными укротителями питомцев Калимдора и Восточных королевств, но твое следующее испытание будет гораздо труднее.$b$bТеперь ты $gдолжен:должна; победить четырех укротителей питомцев Запределья: Ники Мелкотех на полуострове Адского Пламени, Рас\'ана в Зангартопи, Наррока в Награнде и Морулу Древнего в Шаттрате.$b$bТебе нужно одержать над ними верх, чтобы стать сильнее.', '', 'Ты можешь сделать это!', 'Одри Бернхеп', 'Ты и в самом деле делаешь успехи!', 'Одри Бернхеп', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'zhCN', '战斗宠物训练师：外域', '在宠物战斗中击败尼基·微技、拉斯安、纳洛克和莫勒鲁长老。', '你成功击败了卡利姆多和东部王国的驯宠大师，但你的下一项挑战将更为艰难。$B$B你必须在外域击败这四名训练师：地狱火半岛的尼基·微技、赞加沼泽的拉斯安、纳格兰的纳洛克和沙塔斯城的莫勒鲁长老。$B$B要想在成功之路上继续前进，就必须先击败他们。', '', '你能行的！', '奥黛丽·伯恩赫普', '你在成功道路上又迈出了一大步！', '奥黛丽·伯恩赫普', '', 0);
INSERT INTO `quest_template_locale` VALUES (31919, 'zhTW', '戰寵馴獸師：外域', '在寵物對戰中擊敗尼奇‧小型技術、雷森、納羅克以及長者莫魯魯。', '你已經擊敗了卡林多和東部王國的大師級馴獸師，但你接下來的挑戰將會更加困難。$B$B在外域有四名你必須擊敗的馴獸師：地獄火半島的尼奇‧小型技術、贊格沼澤的雷森、納葛蘭的納羅克、以及撒塔斯的長者莫魯魯。$B$B你必須擊敗他們來獲得更大的成就。', '', '你可以做到！', '奧德蕾‧燃憂', '你真的成功了！', '奧德蕾‧燃憂', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'deDE', 'Großmeister Antari', 'Besiegt Blutritter Antari in einem Haustierkampf.', 'Leute wie Euch kenne ich zur Genüge: junge, naive Zähmer, die glauben, dass sie es verdienen, die Siegeslorbeeren auf dem Silbertablett gereicht zu bekommen.$B$BAber nicht mit mir! In mir habt Ihr Euren Meister gefunden, $C. Ich, Blutritter Antari des Schattenmondtals, kann es kaum erwarten, Euch zu zeigen, wie es sich anfühlt, im Kampf ausgelöscht zu werden.', '', 'Ihr habt keine Chance.', 'Blutritter Antari', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'esES', 'Gran maestro Antari', 'Derrota al caballero de sangre Antari en un duelo de mascotas.', 'He visto a muchos domadores como tú: jóvenes e ingenuos. Se piensan que se merecen la victoria entregada en una bandeja de plata.$b$b¡Pues eso se acabó! Yo seré la horma de tu zapato, $c. Yo, el caballero de sangre Antari del Valle Sombraluna, espero la oportunidad de demostrarte lo que se siente cuando te machacan en el campo de batalla.', '', 'Tienes los días contados.', 'Caballero de sangre Antari', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'esMX', 'Gran maestro Antari', 'Derrota al caballero de sangre Antari en un duelo de mascotas.', 'He visto a muchos domadores como tú: jóvenes e ingenuos. Se piensan que se merecen la victoria entregada en una bandeja de plata.$b$b¡Pues eso se acabó! Yo seré la horma de tu zapato, $c. Yo, el caballero de sangre Antari del Valle Sombraluna, espero la oportunidad de demostrarte lo que se siente cuando te machacan en el campo de batalla.', '', 'Tienes los días contados.', 'Caballero de sangre Antari', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'frFR', 'Grand maître Antari', 'Gagnez un combat de mascottes contre le Chevalier de sang Antari.', 'J’ai déjà croisé des gens de votre acabit. $gUn jeune dresseur naïf:une jeune dresseuse naïve; qui croit mériter qu’on lui serve la victoire sur un plateau d’argent.$b$bC’en est assez ! Je vais mettre fin à vos rêves, $c. Moi, chevalier de sang Antari de la vallée d’Ombrelune, j’attends l’occasion de vous montrer ce que ça fait d’être ridiculisé au combat.', '', 'Vous n’avez aucune chance.', 'Chevalier de sang Antari', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'itIT', 'Gran Maestro Antari', 'Sconfiggi il Cavaliere del Sangue Antari in uno scontro tra mascotte.', 'Ne ho già visti tanti come te. Allenatori giovani e ingenui che credono di meritare la vittoria su un piatto d\'argento.$B$BBe\', è ora di finirla! È arrivato il momento di dimostrare quanto vali veramente, $C. Io, il Cavaliere del Sangue Antari della Valle di Torvaluna, ti mostrerò come ci si sente a essere schiacciati in battaglia.', '', 'Non hai alcuna speranza!', 'Cavaliere del Sangue Antari', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'koKR', '특급 조련사 안타리', '애완동물 대전에서 혈기사 안타리를 꺾어야 합니다.', '예전에 그대와 같은 조련사를 본 적이 있습니다. 어리고 무지하면서... 힘든 노력을 들이지 않고도 승리는 당연히 자기들 것이라고 생각하더군요.$B$B이젠 더는 그런 일이 없을 겁니다! 그대가 눈을 뜨도록 해 드리겠습니다, $c님. 어둠달 골짜기의 혈기사인 이 안타리가 전투에서 참패를 겪는다는 게 어떤 느낌인지 알게 해드리겠습니다.', '', '그대가 이길 가능성은 전혀 없습니다.', '혈기사 안타리', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'ptBR', 'Grão-mestre Antari', 'Vença o Cavaleiro Sangrento Antari numa batalha de mascotes.', 'Já vi outros como você antes. Domado$gr:ra; jovem e $gingênuo:ingênua; acha que merece a vitória entregue numa bandeja de prata.$B$BBem, não vai ser mais assim! Eu trarei você à razão, $c. Eu, Cavaleiro Sangrento Antari, do Vale da Lua Negra, aguardo a oportunidade de lhe mostrar como é ser obliterado em combate.', '', 'Você não tem a menor chance.', 'Cavaleiro Sangrento Antari', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'ruRU', 'Великий укротитель Антари', 'Победите рыцаря крови Антари в битве питомцев.', 'Мне приходилось встречать таких, как ты. Таких же молодых и наивных укротителей, которые считают, что победу им должны поднести на блюдечке с голубой каемочкой.$B$BКак же! Настало время настоящей битвы, |3-6($c)! Я, рыцарь крови Антари из Долины Призрачной Луны, собираюсь показать тебе, что значит поражение!', '', 'У тебя нет шансов.', 'Рыцарь крови Антари', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'zhCN', '驯宠宗师安塔里', '在一场宠物战斗中击败血骑士安塔里。', '我以前见过你这样的人。年轻天真的驯宠师，以为胜利都会像馅饼一样凭空砸到自己的头上。$B$B你们的好运到此为止！我会让你尝到教训，$c。我——影月谷的血骑士安塔里，正在等待时机，让你尝到在战斗中一败涂地的滋味。', '', '你毫无胜算。', '血骑士安塔里', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31920, 'zhTW', '宗師安塔利', '在寵物對戰中擊敗血騎士安塔利。', '我看過像你這樣的傢伙。年輕、天真的訓練師，覺得自己天下無敵，勝利唾手可得。$B$B得了吧你！我就是你的天敵，$c。我，影月谷的血騎士安塔利，會讓你嚐嚐在戰鬥中失敗的滋味', '', '你沒機會的。', '血騎士安塔利', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'deDE', 'Kampfhaustierzähmer: Scherbenwelt', 'Besiegt Nicki Mikrotech, Ras\'an, Narrok und Morulu den Älteren in Haustierkämpfen.', 'Ihr habt die Meisterzähmer Kalimdors und der Östlichen Königreiche bezwungen, doch Eure nächste Herausforderung wird Euch auf eine sehr viel härtere Probe stellen.$B$BIn der Scherbenwelt gibt es vier Zähmer, die Ihr jetzt besiegen müsst: Nicki Mikrotech auf der Höllenfeuerhalbinsel, Ras\'an in den Zangarmarschen, Narrok in Nagrand und Morulu den Älteren in Shattrath.$B$BBesiegt sie, wenn Ihr den Pfad zu wahrer Größe beschreiten wollt.', '', 'Mögen Eure Kämpfe ruhmreich sein.', 'Varzok', 'Und noch ein Schritt auf Eurem Weg ist getan.', 'Varzok', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'esES', 'Domadores de mascotas de duelo: Terrallende', 'Derrota a Nicki Pocatécnica, a Ras\'an, a Narrok y a Morulu el Ancestro en un duelo de mascotas.', 'Has superado a los maestros domadores de Kalimdor y los Reinos del Este, pero tu próximo desafío será mucho más difícil.$B$BAhora tendrás que derrotar a cuatro domadores en Terrallende: Nicky Pocatécnica, en la Península del Fuego Infernal; Ras\'an en la Marisma de Zangar; Narrok, en Nagrand y a Morulu el Ancestro, en Shattrath.$B$BDebes vencerlos si quieres avanzar en tu camino hacia la grandeza.', '', '¡Que tus duelos sean gloriosos!', 'Varzok', 'Otra etapa de tu viaje completada.', 'Varzok', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'esMX', 'Domadores de mascotas de duelo: Terrallende', 'Derrota a Nicki Pocatécnica, a Ras\'an, a Narrok y a Morulu el Ancestro en un duelo de mascotas.', 'Has superado a los maestros domadores de Kalimdor y los Reinos del Este, pero tu próximo desafío será mucho más difícil.$B$BAhora tendrás que derrotar a cuatro domadores en Terrallende: Nicky Pocatécnica, en la Península del Fuego Infernal; Ras\'an en la Marisma de Zangar; Narrok, en Nagrand y a Morulu el Ancestro, en Shattrath.$B$BDebes vencerlos si quieres avanzar en tu camino hacia la grandeza.', '', '¡Que tus duelos sean gloriosos!', 'Varzok', 'Otra etapa de tu viaje completada.', 'Varzok', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'frFR', 'Dompteurs de mascottes de combat : l’Outreterre', 'Gagnez un combat de mascottes contre Nicky Minitech, Ras’an, Narrok et Morulu l’Ancien.', 'Vous avez fait mordre la poussière aux maîtres dresseurs de Kalimdor et des royaumes de l’Est, mais votre prochain défi sera bien plus difficile.$b$bVous devez maintenant affronter les quatre dompteurs de l’Outreterre : Nicky Minitech dans la péninsule des Flammes infernales, Ras’an dans le marécage de Zangar, Narrok à Nagrand et Morulu l\'Ancien à Shattrath.$b$bVous devez les vaincre pour poursuivre votre chemin sur les routes de la grandeur.', '', 'Puissent vos combats être glorieux.', 'Varzok', 'Une autre étape couronnée de succès dans votre périple.', 'Varzok', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'itIT', 'Allenatori di mascotte: Terre Esterne', 'Sconfiggi Nicki Microtecno, Ras\'an, Narrok e Morulu l\'Anziano in degli scontri tra mascotte.', 'Hai battuto i Maestri Allenatori di Kalimdor e dei Regni Orientali, ma la prossima sfida sarà molto più ardua.$B$BDovrai sconfiggere quattro allenatori nelle Terre Esterne: Nicki Microtecno nella Penisola del Fuoco Infernale, Ras\'an in Zangarmarsh, Narrok in Nagrand e Morulu l\'Anziano in Shattrath.$B$BDevi avere la meglio se vuoi procedere sul percorso della grandezza.', '', 'Che i tuoi scontri siano gloriosi.', 'Varzok', 'Hai completato un\'altra tappa del viaggio.', 'Varzok', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'koKR', '전투 애완동물 조련사: 아웃랜드', '애완동물 대전에서 니키 타이니테크, 라스안, 나로크, 어르신 모룰루를 꺾어야 합니다.', '칼림도어와 동부 왕국의 뛰어난 조련사들은 모두 꺾었지만, 아직 다음 도전 상대들이 남았소.$B$B아웃랜드에는 당신이 꺾어야 할 네 명의 조련사가 있다오. 지옥불 반도의 니키 타이니테크, 장가르 습지대의 라스안, 나그란드의 나로크, 샤트라스의 어르신 모룰루를 찾아가시오.$B$B위대함의 여정을 계속 가려면 그들을 꺾어야 하오.', '', '영광스러운 전투가 되길 바라오.', '바르조크', '조련사로서의 여정에서 한발 더 나아갔군.', '바르조크', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'ptBR', 'Domadores de Batalha: Terralém', 'Derrote Nicky Minitreco, Ras’an, Narrok e Morulu, o Ancião, numa batalha de mascotes.', 'Você derrotou os melhores domadores de Kalimdor e dos Reinos do Leste, mas seu próximo desafio será bem mais difícil.$B$BHá quatro domadores em Terralém que você precisa derrotar: Nicky Minitreco na Península Fogo do Inferno, Ras\'an no Pântano Zíngaro, Narrok em Nagrand e Morulu, o Ancião, em Shattrath.$B$BVocê precisa derrotá-los para continuar a caminho da grandeza.', '', 'Que suas batalhas sejam gloriosas.', 'Varzok', 'Você completou outra etapa da jornada.', 'Varzok', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'ruRU', 'Укротители боевых питомцев: Запределье', 'Победите Ники Мелкотех, Рас\'ана, Наррока и Морулу Древнего в битвах питомцев.', 'Тебе удалось одержать верх над самыми опытными укротителями питомцев Калимдора и Восточных королевств, но твое следующее испытание будет гораздо труднее.$b$bТеперь ты $gдолжен:должна; победить четырех укротителей питомцев Запределья: Ники Мелкотех на полуострове Адского Пламени, Рас\'ана в Зангартопи, Наррока в Награнде и Морулу Древнего в Шаттрате.$b$bТебе нужно одержать над ними верх, чтобы стать сильнее.', '', 'Пусть битвы принесут тебе славу.', 'Варзок', 'Вот и еще один этап твоего пути завершен.', 'Варзок', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'zhCN', '战斗宠物训练师：外域', '在宠物战斗中击败尼基·微技、拉斯安、纳洛克和莫勒鲁长老。', '你成功击败了卡利姆多和东部王国的驯宠大师，但你的下一项挑战将更为艰难。$B$B你必须在外域击败这四名训练师：地狱火半岛的尼基·微技、赞加沼泽的拉斯安、纳格兰的纳洛克和沙塔斯城的莫勒鲁长老。$B$B要想在成功之路上继续前进，就必须先击败他们。', '', '祝你在战场上能载誉而归。', '瓦佐克', '你的另一段旅途又画上了一个完美的句号。', '瓦佐克', '', 0);
INSERT INTO `quest_template_locale` VALUES (31921, 'zhTW', '戰寵馴獸師：外域', '在寵物對戰中擊敗尼奇‧小型技術、雷森、納羅克以及長者莫魯魯。', '你已經擊敗了卡林多和東部王國的大師級馴獸師，但你接下來的挑戰將會更加困難。$B$B在外域有四名你必須擊敗的馴獸師：地獄火半島的尼奇‧小型技術、贊格沼澤的雷森、納葛蘭的納羅克、以及撒塔斯的長者莫魯魯。$B$B你必須擊敗他們來獲得更大的成就。', '', '希望你打一場好仗。', '伐索克', '在你的旅程中更進一步，完成。', '伐索克', '', 0);
