INSERT INTO `quest_template_locale` VALUES (31934, 'frFR', 'Gerbe-tripes', 'Gagnez un combat de mascottes contre Gerbe-tripes.', 'Moi aimer combattre vous. Vous pas trop facile comme les autres. Nous combattre encore ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31934, 'itIT', 'Sboccabudella', 'Sconfiggi Sboccabudella in uno scontro tra mascotte.', 'Io combattere con te. Tu non facile come altri. Noi combattere?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31934, 'koKR', '구역창자', '애완동물 대전에서 구역창자를 꺾어야 합니다.', '나, 너랑 싸우는 거 좋다. 넌 딴 상대하고 달라. 어렵다. 다시 싸울래?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31934, 'ptBR', 'Vomita-tripa', 'Vença Vomita-tripa numa batalha de mascotes.', 'Eu gosta lutar contra você. Você não fácil como outros. Nós luta de novo?', '', '', '', '', '', '', 0);
