INSERT INTO `quest_template_locale` VALUES (31935, 'deDE', 'Großmeister Payne', 'Besiegt Major Payne in einem Haustierkampf.', '$gDieser:Diese:c; rebellische $C ist also wieder da, um $gseine:ihre; Kräfte mit denen des Großmeisters zu messen. Dieses Mal wird Eure Kühnheit Euer Verderben sein!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31935, 'esES', 'Gran maestro Payne', 'Derrota al mayor Payne en un duelo de mascotas.', '$gEl:La; $c rebelde vuelve para poner a prueba su poder contra el de un gran maestro. ¡Esta vez tu valentía será tu perdición!', '', '', '', '', '', '', 0);
