INSERT INTO `quest_template_locale` VALUES (31970, 'deDE', 'Großmeister Obalis', 'Besiegt Obalis in einem Haustierkampf.', 'Ihr glaubt, Ihr seid bereit, Pandaria herauszufordern? Ihr glaubt wirklich, dass Ihr die mächtigsten Zähmer ganz Azeroths bezwingen könnt?$B$BÜberlasst das Urteil darüber nur mir, $R. Stellt Euch mir auf dem Vir\'naaldamm in Uldum, und es wird sich zeigen, ob Ihr wirklich bereit seid.', '', 'Ich erwarte Euch in der Schlacht.', 'Obalis', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'esES', 'Gran maestro Obalis', 'Derrota a Obalis en un duelo de mascotas.', '¿Crees que estás $glisto:lista; para desafiar a Pandaria? ¿Realmente crees que puedes vencer a los domadores más poderosos de todo Azeroth?$B$BSeré yo quien lo juzgue, $r. Enfréntate a mí en la cima de la Presa Vir\'naal, en Uldum, y veremos si estás $gpreparado:preparada; de verdad.', '', 'Espero con ansia nuestro duelo.', 'Obalis', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'esMX', 'Gran maestro Obalis', 'Derrota a Obalis en un duelo de mascotas.', '¿Crees que estás $glisto:lista; para desafiar a Pandaria? ¿Realmente crees que puedes vencer a los domadores más poderosos de todo Azeroth?$B$BSeré yo quien lo juzgue, $n. Enfréntate a mí en la cima de la Presa Vir\'naal, en Uldum, y veremos si estás $gpreparado:preparada; de verdad.', '', 'Espero con ansia nuestro duelo.', 'Obalis', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'frFR', 'Grand maître Obalis', 'Gagnez un combat de mascottes contre Obalis.', 'Vous vous croyez $gprêt:prête; à relever les défis de la Pandarie ? Vous pensez vraiment pouvoir vaincre les dompteurs les plus puissants de tout Azeroth ?$b$bPermettez-moi d’en être le juge, $R. Affrontez-moi au sommet du barrage de la Vir’naal, à Uldum, et nous verrons si vous êtes aussi $gprêt:prête; que vous le pensez.', '', 'J’attends notre rencontre.', 'Obalis', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'itIT', 'Gran Maestro Obalis', 'Sconfiggi Obalis in uno scontro tra mascotte.', 'Credi di essere $gpronto:pronta; ad affrontare i pericoli di Pandaria? Pensi davvero di poter sconfiggere gli allenatori più esperti di tutta Azeroth?$B$BSarò io a giudicare la tua bravura, $R. Incontriamoci in cima alla Diga di Vir\'naal a Uldum, e vedremo se sei davvero $gpronto:pronta;.', '', 'Attendo con ansia il nostro scontro.', 'Obalis', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'koKR', '특급 조련사 오발리스', '애완동물 대전에서 오발리스를 꺾어야 합니다.', '판다리아에 도전할 준비가 된 것 같다고 생각하는 거요? 진정 아제로스를 통틀어 가장 강력한 조련사들과 싸울 수 있다고 믿고 있소?$B$B그렇다면 내가 확인해주겠소. 울둠의 비르나알 둑 꼭대기로 날 찾아오시오. 당신이 정말로 준비되었는지 한 번 봅시다.', '', '대결할 날을 기다리고 있겠소.', '오발리스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'ptBR', 'Grão-mestre Obalis', 'Vença Obalis numa batalha de mascotes.', 'Você acha que está pront$Go:a; para desafiar Pandária? Você realmente acredita que pode derrotar os domadores mais poderosos de Azeroth?$B$BEu serei juiz dessa sua crença, $r. Enfrente-me no topo do Dique Vir\'naal, em Uldum, e veremos se você realmente está preparad$Go:a;.', '', 'Espero por nossa batalha.', 'Obalis', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'ruRU', 'Великий укротитель Обалис', 'Победите Обалиса в битве питомцев.', 'Думаешь, ты $gготов:готова; бросить вызов Пандарии? Ты правда считаешь, что можешь победить величайших укротителей Азерота?$B$BПосмотрим, |3-6($r). Встретимся у запруды Вир\'наал в Ульдуме и посмотрим, на что ты $gспособен:способна;.', '', 'Жду не дождусь битвы с тобой.', 'Обалис', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'zhCN', '驯宠宗师奥巴里斯', '在宠物战斗中击败奥巴里斯。', '你觉得自己已经准备好进军潘达利亚了？你真以为自己能击败全艾泽拉斯最强大的驯宠师？$B$B我会试试你的实力，$r。到奥丹姆的维尔纳尔水坝顶上来找我，让我们瞧瞧，你究竟准备好了没有。', '', '我等待着我们的对决。', '奥巴里斯', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31970, 'zhTW', '宗師歐巴里斯', '在寵物對戰中擊敗歐巴里斯。', '你覺得自己準備好挑戰潘達利亞了？你真的相信你能擊敗艾澤拉斯上最強大訓練師？$B$B我會為你評判這個念頭是不是真的，$r。到奧丹姆頂端的維爾納爾水壩面對我，讓我們看看你是否真的準備好了。', '', '期待我們的戰鬥。', '歐巴里斯', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'deDE', 'Großmeister Obalis', 'Besiegt Obalis in einem Haustierkampf.', 'Siehe da - $gder triumphierende Zähmer:die triumphierende Zähmerin; will $gseine:ihre; Kräfte mit den meinen messen. So sei es. Ich nehme die Herausforderung an.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'esES', 'Gran maestro Obalis', 'Derrota a Obalis en un duelo de mascotas.', 'Veo que $gel glorioso domador:la gloriosa domadora; regresa para medirse conmigo una vez más. Muy bien. Acepto tu desafío.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'esMX', 'Gran maestro Obalis', 'Derrota a Obalis en un duelo de mascotas.', 'Veo que $gel glorioso domador:la gloriosa domadora; regresa para medirse conmigo una vez más. Muy bien. Acepto tu desafío.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'frFR', 'Grand maître Obalis', 'Gagnez un combat de mascottes contre Obalis.', 'Je vois $gle dompteur triomphant:la dompteuse triomphante; revenir à moi pour tester sa puissance. Très bien. J’accepte votre défi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'itIT', 'Gran Maestro Obalis', 'Sconfiggi Obalis in uno scontro tra mascotte.', 'Vedo che sei $gtornato:tornata; trionfante per affrontarmi un\'altra volta. Molto bene. Accetto la tua sfida.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'koKR', '특급 조련사 오발리스', '애완동물 대전에서 오발리스를 꺾어야 합니다.', '용기백배한 조련사가 나를 상대로 다시 시험을 받으러 왔군. 좋소. 도전을 받아들이겠소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'ptBR', 'Grão-mestre Obalis', 'Vença Obalis numa batalha de mascotes.', 'Vejo que $Go:a; domado$Gr:ra; triunfante retorna para testar suas forças contra mim. Pois bem. Aceito seu desafio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'ruRU', 'Великий укротитель Обалис', 'Победите Обалиса в битве питомцев.', 'Вижу, великий укротитель снова здесь. Хочешь сразиться еще раз? Хорошо. Я принимаю твой вызов.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'zhCN', '驯宠宗师奥巴里斯', '在宠物战斗中击败奥巴里斯。', '看来大获全胜的驯宠师又来找我检验$G他自己:她自己;的实力了。很好。我接受你的挑战。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31971, 'zhTW', '宗師歐巴里斯', '在寵物對戰中擊敗歐巴里斯。', '凱旋而去的訓練師再度回來挑戰我，測試$G他:她;的技能。很好。我接受你的挑戰。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'deDE', 'Brok', 'Besiegt Brok in einem Haustierkampf.', 'Noch verstehe ich die Not dieser Geschöpfe nicht genug, um ihnen angemessen Hilfe leisten zu können. Ob ein weiterer Kampf unseren Bund wohl stärken könnte?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'esES', 'Brok', 'Derrota a Brok en un duelo de mascotas.', 'Aún no comprendo lo suficientemente bien la situación de estas criaturas como para poder ayudarlas. Es posible que otro duelo fortalezca nuestros lazos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'esMX', 'Brok', 'Derrota a Brok en un duelo de mascotas.', 'Aún no comprendo lo suficientemente bien la situación de estas criaturas como para poder ayudarlas. Es posible que otro duelo fortalezca nuestros lazos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'frFR', 'Brok', 'Gagnez un combat de mascottes contre Brok.', 'Je ne ressens pas encore assez bien la détresse de ces créatures pour pouvoir les aider comme je le voudrais. Peut-être qu’un autre combat renforcerait nos liens ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'itIT', 'Brok', 'Sconfiggi Brok in uno scontro tra mascotte.', 'Non riesco ancora a capire la vera forza di queste creature. Forse un altro scontro potrebbe rafforzare il nostro legame?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'koKR', '브로크', '애완동물 대전에서 브로크를 꺾어야 합니다.', '저 생물들도 이 정도 일을 겪었으면 알아먹을 만한데... 아직도 이해를 못 하겠군. 한 판 더 싸우면 좀 친해지려나?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'ptBR', 'Brok', 'Vença Brok numa batalha de mascotes.', 'Ainda não compreendo o suficiente a desgraça dessas criaturas para ajudá-las adequadamente. Quem sabe outra luta possa fortalecer nossa relação.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'ruRU', 'Брок', 'Победите Брока в битве питомцев.', 'Я пока не очень хорошо понимаю, чего от меня хотят эти крохи. Может, еще одна битва укрепит связь между нами?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'zhCN', '布洛克', '在宠物战斗中击败布洛克。', '我还不是很理解这些生物所处的困境，所以无法充分帮助它们。也许再战一场能加强我们的联结？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31972, 'zhTW', '布拉克', '在寵物對戰中擊敗布拉克。', '我還沒完全了解這些生物的境況以有效率地幫助牠們。也許再戰鬥一次能加深我們之間的連結？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'deDE', 'Bordin Ruhigfaust', 'Besiegt Bordin Ruhigfaust in einem Haustierkampf.', 'Die Eingeborenen haben mich die Kraft der Erde gelehrt. Ich weiß um ihre unerschöpfliche Beständigkeit. Lasst uns ein Mal mehr kämpfen, und Ihr werdet sehen, dass ich zäher bin als je zuvor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'esES', 'Bordin Puñofirme', 'Derrota a Bordin Puñofirme en un duelo de mascotas.', 'Los nativos me han enseñado la fuerza de la tierra. Conozco su resistencia imperecedera. ¡Luchemos una vez más y verás que mi fuerza es mayor que nunca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'esMX', 'Bordin Puñofirme', 'Derrota a Bordin Puñofirme en un duelo de mascotas.', 'Los nativos me han enseñado la fuerza de la tierra. Conozco su resistencia imperecedera. ¡Luchemos una vez más y verás que mi fuerza es mayor que nunca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'frFR', 'Bordin Poing Hardi', 'Gagnez un combat de mascottes contre Bordin Poing Hardi.', 'Les indigènes m’ont inculqué la force de la terre. J’ai fait mienne son endurance infinie. Livrons-nous bataille à nouveau et vous verrez que je suis plus fort que jamais !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'itIT', 'Bordin Pugnofermo', 'Sconfiggi Bordin Pugnofermo in uno scontro tra mascotte.', 'I nativi mi hanno mostrato la forza della terra. Conosco il suo eterno potere. Scontriamoci un\'altra volta, e vedrai che sono più forte che mai!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'koKR', '보르딘 스테디피스트', '애완동물 대전에서 보르딘 스테디피스트를 꺾어야 합니다.', '원주민들은 내게 대지의 힘에 대해 알려주었소. 나는 끝없는 인내를 배웠지. 자, 다시 싸워봅시다. 내가 전보다 더 강해졌단 걸 알게 될 거요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'ptBR', 'Bordin Punhofirme', 'Vença Bordin Punhofirme numa batalha de mascotes.', 'Os nativos me ensinaram a força da terra. Conheço sua infindável resistência. Lutemos outra vez, e você verá que eu estou mais forte do que nunca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'ruRU', 'Бордин Крепкорук', 'Победите Бордина Крепкорука в битве питомцев.', 'Местные жители научили меня чувствовать силу земли. Ее поддержка никогда не ослабевает. Давай сразимся еще раз, и ты увидишь, как я силен!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'zhCN', '鲍丁·固拳', '在宠物战斗中击败鲍丁·固拳。', '当地人教我领会到了大地之力。我明白那是一种无尽的坚忍。让我们再战一场吧，你会发现，我变得比以前更难对付！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31973, 'zhTW', '博丁‧穩拳', '在寵物對戰中擊敗博丁‧穩拳。', '本地的原住民教導我大地的力量。我認識到了那無盡的堅忍。讓我們再次戰鬥吧，你會發現我比以往任何時候都要強！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'deDE', 'Goz Unheilsfuror', 'Besiegt Goz Unheilsfuror in einem Haustierkampf.', 'Die Macht des Zwielichts durchdringt mich. Ich bin ein Gott! Ihr habt keine Chance, mich zu besiegen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'esES', 'Goz Malafuria', 'Derrota a Goz Malafuria en un duelo de mascotas.', 'El poder del crepúsculo corre por mis venas. ¡Soy un dios! ¡No puedes esperar derrotarme!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'esMX', 'Goz Malafuria', 'Derrota a Goz Malafuria en un duelo de mascotas.', 'El poder del crepúsculo corre por mis venas. ¡Soy un dios! ¡No puedes esperar derrotarme!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'frFR', 'Goz Furie du Fléau', 'Gagnez un combat de mascottes contre Goz Plaie du Fléau.', 'La puissance du crépuscule coule en moi. Je suis un dieu ! Vous n’avez aucun espoir de me vaincre !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'itIT', 'Goz Furiamaligna', 'Sconfiggi Goz Furiamaligna in uno scontro tra mascotte.', 'Il potere del crepuscolo scorre dentro di me. Sono un dio! Non puoi sconfiggermi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'koKR', '고즈 베인퓨리', '애완동물 대전에서 고즈 베인퓨리를 꺾어야 합니다.', '황혼의 힘이 내 안에 넘치는군. 나는 신이다! 날 이길 생각은 꿈도 꾸지 마시오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'ptBR', 'Goz Malefúria', 'Vença Goz Malefúria numa batalha de mascotes.', 'O poder do crepúsculo flui em mim. Sou um deus! Você não tem a menor chance de me derrotar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'ruRU', 'Гоз Гибельная Ярость', 'Победите Гоза Гибельную Ярость в битве питомцев.', 'По моим венам течет сила сумерек. Я – бог! У тебя нет шансов против меня!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'zhCN', '高兹·怨怒', '在宠物战斗中击败高兹·怨怒。', '暮光之力在我体内涌动。我就是神！你没希望打败我！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31974, 'zhTW', '苟斯‧禍怒', '在寵物對戰中擊敗苟斯‧禍怒。', '暮光的力量在我身上流竄。我是個神！你沒有擊敗我的希望！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'deDE', 'Die Rückkehr des Champions', 'Meldet Euch bei Marlene Trichdie in Sturmwind.', 'Wenn Ihr mich fragt, gehört Ihr in die Scherbenwelt, zusammen mit den anderen Hitzköpfen. Warum geht Ihr nicht wieder zu Marlene Trichdie in Sturmwind und findet heraus, was Eure neue Karriere als $gZähmer:Zähmerin; als Nächstes bereithält?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'esES', 'El regreso de los campeones', 'Ve a ver a Audrey Burnhep en Ventormenta.', 'Si quieres mi opinión, deberías estar en Terrallende, con los demás ases. ¿Por qué no vuelves a ver a Audrey Burnhep, en Ventormenta, para ver cuál es el siguiente paso de tu carrera como $gdomador:domadora;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'esMX', 'El regreso de los campeones', 'Ve a ver a Audrey Burnhep en Ventormenta.', 'Si quieres mi opinión, deberías estar en Terrallende, con los demás ases. ¿Por qué no vuelves a ver a Audrey Burnhep, en Ventormenta, para ver cuál es el siguiente paso de tu carrera como $gdomador:domadora;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'frFR', 'Le retour du champion', 'Allez vous présenter à Audrey Burnhep à Hurlevent.', 'Si vous voulez mon avis, votre place, elle est en Outreterre avec les autres champions. Pourquoi vous iriez pas voir Audrey Burnhep à Hurlevent pour connaître la prochaine étape dans votre nouvelle carrière de $gdompteur:dompteuse; ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'itIT', 'Il ritorno del campione', 'Fai rapporto a Audrey Burnhep a Roccavento.', 'Se vuoi la mia opinione, la tua bravura è pari a quella dei migliori allenatori delle Terre Esterne. Perché non torni da Audrey Burnhep a Roccavento e scopri cosa ti aspetta nella tua carriera da $gallenatore:allenatrice;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'koKR', '용사의 귀환', '스톰윈드에 있는 오드리 번헵에게 보고해야 합니다.', '굳이 묻는다면 당신은 다른 실력 있는 자들과 함께 아웃랜드에 걸맞은 조련사예요. 스톰윈드에 있는 오드리 번헵에게 돌아가서 조련사로 경력을 쌓으려면 뭘 해야 할지 알아보는 건 어떠세요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'ptBR', 'O campeão retorna', 'Apresente-se a Audreia Répibãrne em Ventobravo.', 'Eu acho que o teu lugar é em Terralém com os outros figurões. Melhor cê falar com Audreia Répibãrne lá em Ventobravo pra ver qual o próximo passo na sua nova carreira de domado$Gr:ra;!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'ruRU', 'Возвращение героя', 'Обратитесь к Одри Бернхеп в Штормграде.', 'Я считаю, что тебе самое место среди всяких дельцов из Запределья. Возвращайся-ка в Штормград к Одри Бернхеп и спроси у нее, что делать дальше!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'zhCN', '勇者归来', '去暴风城找奥黛丽·伯恩赫普。', '在我看来，你跟其他那些高手一样，都属于外域。为什么不回暴风城找奥黛丽·伯恩赫普，看看作为新晋的驯宠师，下一步该做什么！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31975, 'zhTW', '勇士回歸', '到暴風城找奧德蕾‧燃憂。', '問我的話，我覺得你應該留在外域和其他好傢伙在一起。你為什麼不回暴風城去找奧德蕾‧燃憂，看看你身為訓練師的下一步該怎麼走？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'deDE', 'Die Rückkehr des Champions', 'Meldet Euch bei Marlene Trichdie in Sturmwind.', 'Die Kunde von Eurem Können verbreitet sich. Ich schlage vor, Ihr kehrt für weitere Unterweisung zu Eurer Trainerin Marlene Trichdie zurück.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'esES', 'El regreso de los campeones', 'Ve a ver a Audrey Burnhep en Ventormenta.', 'Cada vez se habla más de tus hazañas. Te sugiero que vuelvas a ver a tu entrenadora, Audrey Burnhep, para que te aconseje.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'esMX', 'El regreso de los campeones', 'Ve a ver a Audrey Burnhep en Ventormenta.', 'Cada vez se habla más de tus hazañas. Te sugiero que vuelvas a ver a tu entrenadora, Audrey Burnhep, para que te aconseje.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'frFR', 'Le retour du champion', 'Allez vous présenter à Audrey Burnhep à Hurlevent.', 'La rumeur de votre talent se répand comme une traînée de poudre. Je vous suggère de retourner voir Audrey Burnhep, votre entraîneur. Elle saura vous guider.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'itIT', 'Il ritorno del campione', 'Fai rapporto a Audrey Burnhep a Roccavento.', 'In giro si parla molto delle tue abilità. Ti suggerisco di tornare dalla tua allenatrice, Audrey Burnhep, per continuare ad affinarle.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'koKR', '용사의 귀환', '스톰윈드에 있는 오드리 번헵에게 보고해야 합니다.', '실력이 뛰어나다는 소문이 자자하더군요. 당신의 훈련관인 오드리 번헵에게 돌아가 앞으로 할 일에 대해 알아보는 게 좋을 거예요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31976, 'ptBR', 'O campeão retorna', 'Apresente-se a Audreia Répibãrne em Ventobravo.', 'As notícias de sua destreza se espalham. Sugiro que você volte à sua instrutora, Audreia Répibãrne, para mais orientações.', '', '', '', '', '', '', 0);
