INSERT INTO `quest_template_locale` VALUES (31976, 'zhCN', '勇者归来', '去暴风城找奥黛丽·伯恩赫普。', '关于你卓绝战技的传闻已经铺天盖地。我建议你回去找训练师奥黛丽·伯恩赫普，寻求下一步指引。', '', '', '', '', '', '', 0);
