INSERT INTO `quest_template_locale` VALUES (31977, 'deDE', 'Die Rückkehr des Champions', 'Meldet Euch bei Varzok in Orgrimmar.', 'Wenn Ihr mich fragt, gehört Ihr in die Scherbenwelt, zusammen mit den anderen Hitzköpfen. Warum kehrt Ihr nicht zu Varzok in Orgrimmar zurück und findet heraus, was Eure neue Karriere als $gZähmer:Zähmerin; als Nächstes bereithält?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31977, 'esES', 'El regreso de los campeones', 'Ve a ver a Varzok en Orgrimmar.', 'Si quieres mi opinión, deberías estar en Terrallende, con los demás ases. ¿Por qué no vuelves a ver a Varzok, en Orgrimmar, para ver cuál es el siguiente paso de tu carrera como $gdomador:domadora;?', '', '', '', '', '', '', 0);
