INSERT INTO `quest_template_locale` VALUES (31985, 'koKR', '금의환향', '스톰윈드에 있는 오드리 번헵에게 보고해야 합니다.', '당신은 이제 훈련관의 말대로 준비를 갖추었소. 스톰윈드로 돌아가 오드리 번헵에게 보고하시오. 앞으로 할 일을 알려줄 거요.', '', '', '', '', '', '', 0);
