INSERT INTO `quest_template_locale` VALUES (32001, 'itIT', 'Una degna sfida: Sha dell\'Odio', 'Completa il Monastero degli Shandaren in modalità Sfida.', '$n, ho bisogno di allestire un gruppo tranquillo da inviare al Monastero degli Shandaren. Attivate il Globo dello Sfidante che trovate all\'interno e salvate Taran Zhu dallo Sha dell\'Odio.$B$BTornate da me quando avrete portato a termine questa sfida e vi ricompenserò adeguatamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32001, 'ptBR', 'Um desafio digno: Sha do Ódio', 'Completar o Modo Desafio do Monastério Shado-pan.', '$n, você tem de reunir um grupo muito tranquilo e liderá-lo até o Monastério Shado-pan. Lá dentro, ative o Orbe do Desafiante e resgate Taran Zhu das garras do Sha do Ódio.$B$BFale novamente comigo quando completar esse desafio, e eu $Go:a; recompensarei magnificamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32001, 'ruRU', 'Серьезное испытание: Ша Ненависти', 'Пройдите Монастырь Шадо-Пан в режиме испытания.', 'Я хочу, чтобы ты $gсобрал:собрала; спокойную группу и $gотправился:отправилась; в Монастырь Шадо-Пан. Когда прибудешь на место, задействуй сферу испытания и спаси Тажаня Чжу из плена Ша Ненависти.$B$BВозвращайся ко мне за наградой, когда завершишь испытание.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32003, 'deDE', 'Eine würdige Herausforderung: Raigonn', 'Schließt das Tor der Untergehenden Sonne im Herausforderungsmodus ab.', '$n, Ihr müsst eine entschlossene Gruppe zusammenstellen und sie zum Tor der Untergehenden Sonne führen. Aktiviert die Kugel des Herausforderers und vernichtet Raigonn.$B$BKehrt zu mir zurück, wenn Ihr diese Herausforderung bestanden habt, und ich werde Euch fürstlich belohnen.', '', '', '', '', '', '', 0);
