INSERT INTO `quest_template_locale` VALUES (32003, 'ptBR', 'Um desafio digno: Raigonn', 'Completar o Modo Desafio do Portal do Sol Poente.', '$n, você tem de reunir um grupo vigoroso e liderá-lo até o Portal do Sol Poente. Lá dentro, ative o Orbe do Desafiante e destrua Raigonn.$B$BFale novamente comigo quando completar esse desafio, e eu $Go:a; recompensarei magnificamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32003, 'ruRU', 'Серьезное испытание: Райгонн', 'Пройдите Врата Заходящего Солнца в режиме испытания.', 'Я хочу, чтобы ты $gсобрал:собрала; могучую группу и $gотправился:отправилась; к Вратам Заходящего Солнца. Когда прибудешь на место, задействуй сферу испытания и сокруши Райгонна.$B$BВозвращайся ко мне за наградой, когда завершишь это испытание.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32004, 'deDE', 'Eine würdige Herausforderung: Schwadronsführer Ner\'onok', 'Schließt den Niuzaotempel im Herausforderungsmodus ab.', '$n, Ihr müsst eine scharfsichtige Gruppe zusammenstellen und sie zur Belagerung des Niuzaotempels führen. Aktiviert die Kugel des Herausforderers und bringt Schwadronsführer Ner\'onok zu Boden.$B$BKehrt zu mir zurück, wenn Ihr diese Herausforderung bestanden habt, und ich werde Euch fürstlich belohnen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32004, 'esES', 'Un desafío digno: líder de escuadrón Ner\'onok', 'Completa el Asedio del Templo de Niuzao en modo desafío.', '$n, necesito que reúnas un grupo con la vista aguda y lo lleves al Asedio del Templo de Niuzao. Activa el orbe de contendiente que hay dentro y derriba al líder de escuadrón Ner\'onok.$B$BVuelve aquí cuando hayas completado este desafío y te recompensaré generosamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32005, 'esES', 'Un desafío digno: Durand', 'Completa el Monasterio Escarlata en modo desafío.', '$n, necesito que reúnas un grupo fuerte y lo lleves al Monasterio Escarlata. Activa el orbe de contendiente que hay dentro y machaca a Durand con toda tu furia.$B$BVuelve aquí cuando hayas completado este desafío y te recompensaré generosamente.', '', '', '', '', '', '', 0);
