INSERT INTO `quest_template_locale` VALUES (32005, 'koKR', '보람찬 도전: 듀란드', '붉은십자군 수도원 도전 모드를 완료해야 합니다.', '$n, 강인한 파티를 구성하여 붉은십자군 수도원으로 향하게. 그 안에 있는 도전자의 보주를 작동시키고, 정의로운 분노로써 듀란드에게 천벌을 내리게.$B$B도전에 성공한 후 내게 오면 후하게 사례하겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32005, 'ruRU', 'Серьезное испытание: Дюранд', 'Пройдите Монастырь Алого ордена в режиме испытания.', 'Я хочу, чтобы ты $gсобрал:собрала; храбрую группу и $gотправился:отправилась; в Монастырь Алого ордена. Когда прибудешь на место, задействуй сферу испытания и покарай Дюранда.$B$BВозвращайся ко мне за наградой, когда завершишь испытание.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32005, 'zhCN', '艰难的挑战：杜兰德', '完成血色修道院挑战。', '$n，我要你去召集一支顽强的队伍，前往血色修道院。你要激活那里的挑战者的宝珠，并制裁杜兰德。$B$B完成这个挑战后，你再回来找我。我会好好奖赏你的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32005, 'zhTW', '夠格的挑戰:杜蘭德', '完成血色修道院挑戰模式。', '$n，我要你募集一支隊伍，並領往血色修道院。在修道院內，你必須啟動挑戰者之球，並重擊嚴懲杜蘭德。$B$B完成這項挑戰後回來找我，我會好好獎勵你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32006, 'esMX', 'Un desafío digno: tejedor de fuego Koegler', 'Completa las Cámaras Escarlata en modo desafío.', '$n, necesito que reúnas un grupo avezado y te adentres en las profundidades de las Cámaras Escarlata. Activa el orbe de contendiente que hay allí y pon fin a la quema de libros del tejedor de fuego Koegler.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32006, 'itIT', 'Una degna sfida: Koegler il Mistico delle Fiamme', 'Completa le Sale Scarlatte in modalità Sfida.', '$n, ho bisogno di allestire un gruppo scaltro da inviare nelle profondità delle Sale Scarlatte. Attivate il Globo dello Sfidante che trovate all\'interno e ponete fine alla distruzione di libri da parte di Koegler il Mistico delle Fiamme.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32006, 'ptBR', 'Um desafio digno: Tecechamas Koegler', 'Completar o Modo Desafio dos Salões Escarlates.', '$n, eu preciso que você reúna um grupo astuto e se aventure nas profundezas dos Salões Escarlates. Ative o Orbe do Desafiante lá dentro e faça com que o Tecechamas Koegler pare de queimar livros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32007, 'koKR', '보람찬 도전: 암흑스승 간들링', '스칼로맨스 도전 모드를 완료해야 합니다.', '$n, 용감한 파티를 구성하여 스칼로맨스 깊은 곳으로 향하게. 그 안에 있는 도전자의 보주를 작동시키고, 사악한 암흑스승 간들링을 처치하게.$B$B도전에 성공한 후 내게 오면 후하게 사례하겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32007, 'ruRU', 'Серьезное испытание: темный магистр Гандлинг', 'Пройдите Некроситет в режиме испытания.', 'Я хочу, чтобы ты $gсобрал:собрала; бесстрашную группу и $gотправился:отправилась; в Некроситет. Когда прибудешь на место, задействуй сферу испытания и очисти Некроситет от тлетворного влияния темного магистра Гандлинга.$B$BВозвращайся ко мне за наградой, когда завершишь испытание.', '', '', '', '', '', 'Вернитесь к мастеру испытаний Суну в Святилище Семи Звезд, что в Вечноцветущем доле.', 0);
INSERT INTO `quest_template_locale` VALUES (32007, 'zhTW', '夠格的挑戰:黑暗院長加丁', '完成通靈學院挑戰模式。', '$n，我要你募集一支隊伍，並深入通靈學院。在學院內，你必須啟動挑戰者之球，並消滅黑暗院長加丁的邪惡存在。$B$B完成這項挑戰後回來找我，我會好好獎勵你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'deDE', 'Marlene Trichdie', 'Meldet Euch bei Marlene Trichdie in Sturmwind.', 'Seid gegrüßt, $n! Ich bin Marlene Trichdie. Man hört in letzter Zeit ziemlich viel von Euch. Ihr scheint zu $geinem hervorragenden Zähmer:einer hervorragenden Zähmerin; heranzureifen!$B$BIhr solltet Euch mit mir in Sturmwind auf ein Gespräch treffen. Ich stehe mit den besten Zähmern in ganz Azeroth in Verbindung und kann Euch helfen, noch besser zu werden!', '', 'Ich kann es kaum erwarten, Euch zu treffen!', 'Marlene Trichdie', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'esES', 'Audrey Burnhep', 'Ve a ver a Audrey Burnhep en Ventormenta.', '¡Saludos, $n! Mi nombre es Audrey Burnhep. He oído hablar mucho de ti últimamente. ¡Te estás convirtiendo en $gtodo un domador:toda una domadora;!$B$BDeberías venir a verme a Ventormenta. Conozco a algunos de los mejores domadores de todo Azeroth y puedo ayudarte a llegar mucho más lejos.', '', '¡Estoy deseando conocerte!', 'Audrey Burnhep', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'esMX', 'Audrey Burnhep', 'Ve a ver a Audrey Burnhep en Ventormenta.', '¡Saludos, $n! Mi nombre es Audrey Burnhep. He oído hablar mucho de ti últimamente. ¡Te estás convirtiendo en $gtodo un domador:toda una domadora;!$B$BDeberías venir a verme a Ventormenta. Conozco a algunos de los mejores domadores de todo Azeroth y puedo ayudarte a llegar mucho más lejos.', '', '¡Estoy deseando conocerte!', 'Audrey Burnhep', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'frFR', 'Audrey Burnhep', 'Allez vous présenter à Audrey Burnhep à Hurlevent.', 'Salutations, $n ! Mon nom est Audrey Burnhep. J’ai beaucoup entendu parler de vous dernièrement. Vous êtes en train de devenir $gun sacré dompteur:une sacrée dompteuse; !$B$BVenez me retrouver à Hurlevent. Je connais la plupart des meilleurs dompteurs d’Azeroth et je peux vous aider à vous perfectionner davantage.', '', 'J’ai hâte de vous rencontrer !', 'Audrey Burnhep', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'itIT', 'Audrey Burnhep', 'Fai rapporto a Audrey Burnhep a Roccavento.', 'Salve, $n! Il mio nome è Audrey Burnhep. Ho sentito parlare molto di te recentemente. Stai diventando $gun allenatore famoso:un\'allenatrice famosa;!$B$BDovresti venire da me a Roccavento. Ho conoscenze tra i più bravi allenatori di Azeroth, e posso aiutarti a migliorare ancora!', '', 'Non vedo l\'ora di incontrarti!', 'Audrey Burnhep', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'koKR', '오드리 번헵', '스톰윈드에 있는 오드리 번헵에게 보고해야 합니다.', '반가워요, $n 님! 저는 오드리 번헵이라고 해요. 최근에 당신 이야기를 정말 많이들었답니다! 당신은 꽤 훌륭한 조련사가 되어가고 있더군요!$B$B스톰윈드로 오셔서 저와 이야기 좀 나누셔야겠어요. 저는 아제로스에서 가장 훌륭한 조련사들을 많이 알고 있으니 당신이 조련사로 더욱 크게 성장하도록 도와줄 수 있을 거예요!', '', '어서 만나요!', '오드리 번헵', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'ptBR', 'Audreia Répibãrne', 'Apresente-se a Audreia Répibãrne em Ventobravo.', 'Saudações, $n! Meu nome é Audreia Répibãrne. Eu ando ouvindo muita coisa sobre você ultimamente. Você está virando $gum domador:uma domadora; e tanto!$B$BVocê devia vir falar comigo aqui em Ventobravo. Eu tenho contato com alguns dos melhores domadores de toda Azeroth, e eu posso ajudar você a ficar ainda melhor!', '', 'Mal posso esperar para conhecer você!', 'Audreia Répibãrne', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'ruRU', 'Одри Бернхеп', 'Обратитесь к Одри Бернхеп в Штормграде.', 'Приветствую тебя, $n! Меня зовут Одри Бернхеп. Я много о тебе слышала. Ты становишься $gизвестным укротителем:известной укротительницей; питомцев!$b$bТебе следует поговорить со мной в Штормграде. Я знакома со многими из лучших укротителей питомцев Азерота, и я могу помочь тебе стать еще опытнее!', '', 'Не могу дождаться встречи с тобой!', 'Одри Бернхеп', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'zhCN', '奥黛丽·伯恩赫普', '去暴风城向奥黛丽·伯恩赫普报到。', '你好，$n！我叫奥黛丽·伯恩赫普。近来我听说了不少关于你的消息。你正在成为一名出色的驯宠师！$B$B你应该到暴风城找我谈谈。我与艾泽拉斯各地的精英驯宠师都有所往来，我能帮你变得更强！', '', '我想马上与你见面！', '奥黛丽·伯恩赫普', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32008, 'zhTW', '奧德蕾‧燃憂', '到暴風城找奧德蕾‧燃憂。', '歡迎，$n!我的名字是奧德蕾‧燃憂。我最近經常聽說你的事蹟。你已經成為了不起的訓練師了！$B$B你應該來暴風城和我說話。我和艾澤拉斯各地最出色的訓練師都有聯繫，我能幫你更加成長！', '', '等不及和你見面囉！', '奧德蕾‧燃憂', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'deDE', 'Varzok', 'Meldet Euch bei Varzok in Orgrimmar.', 'Lok\'tar, $n. Mein Name ist Varzok, und ich habe in letzter Zeit viel von Euren Leistungen als $gZähmer:Zähmerin; gehört. Ich möchte Euch gerne Trainern in ganz Azeroth vorstellen, damit Ihr Eure Fertigkeiten weiter verbessern könnt.$B$BBitte trefft Euch auf ein Gespräch mit mir in Orgrimmar, auf dass wir alles in die Wege leiten können.', '', 'Ich freue mich schon auf unser Treffen.', 'Varzok', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'esES', 'Varzok', 'Ve a ver a Varzok en Orgrimmar.', 'Lok\'tar, $n. Me llamo Varzok. Últimamente he oído hablar mucho de tus hazañas como $gdomador:domadora; y me gustaría presentarte a instructores de todo Azeroth para que puedas seguir perfeccionando tus habilidades.$B$BVen a verme a Orgrimmar para que podamos empezar.', '', 'Estoy deseando conocerte.', 'Varzok', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'esMX', 'Varzok', 'Ve a ver a Varzok en Orgrimmar.', 'Lok\'tar, $n. Me llamo Varzok. Últimamente he oído hablar mucho de tus hazañas como $gdomador:domadora; y me gustaría presentarte a instructores de todo Azeroth para que puedas seguir perfeccionando tus habilidades.$B$BVen a verme a Orgrimmar para que podamos empezar.', '', 'Estoy deseando conocerte.', 'Varzok', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'frFR', 'Varzok', 'Présentez-vous à Varzok à Orgrimmar.', 'Loktar, $n. Mon nom est Varzok. J\'ai beaucoup entendu parler de vos exploits de $gdompteur:dompteuse;, dernièrement et je voudrais vous présenter des dresseurs d\'Azeroth afin de continuer à vous améliorer.$B$BVenez me parler à Orgrimmar afin que nous puissions nous y mettre.', '', 'J’ai hâte de vous rencontrer.', 'Varzok', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'itIT', 'Varzok', 'Parla con Varzok a Orgrimmar.', 'Lok\'tar, $n. Il mio nome è Varzok. Ho sentito parlare molto delle tue imprese come allenatore recentemente, e vorrei presentarti agli istruttori di tutta Azeroth, in modo che tu possa perfezionare le tue tecniche di combattimento.$B$BParla con me, così potremo cominciare.', '', 'Non vedo l\'ora di incontrarti.', 'Varzok', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'koKR', '바르조크', '오그리마에 있는 바르조크에게 보고해야 합니다.', '록타르, $n. 나는 바르조크라고 하오. 최근 당신이 조련사로서 이뤄낸 성과에 대해 아주 많은 이야기를 들었소. 그래서 당신의 능력을 더욱 갈고닦을 수 있도록 아제로스에 있는 조련사들을 소개해주고 싶구려. 오그리마로 와서 내게 이야기하면 시작하도록 하지.', '', '당신과 만나기를 기다리고 있겠소.', '바르조크', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'ptBR', 'Varzok', 'Apresente-se a Varzok em Orgrimmar.', 'Lok\'tar, $n. Meu nome é Varzok. Eu ouvi muitas coisas boas sobre suas habilidades de $gdomador:domadora;, e gostaria de apresentar você a instrutores de Azeroth que podem ajudá-l$go:a; a ficar ainda melhor.$B$BPor favor fale comigo em Orgrimmar para que possamos começar.', '', 'Aguardo ansioso por nosso encontro.', 'Varzok', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'ruRU', 'Варзок', 'Поговорите с Варзоком в Оргриммаре.', 'Лок\'тар, $n. Меня зовут Варзок. Я многое слышал о твоих успехах в качестве $gукротителя:укротительницы; боевых питомцев, и я хочу познакомить тебя с другими укротителями Азерота, чтобы ты $gпродолжал:продолжала; оттачивать свои навыки.$b$bПоговори со мной в Оргриммаре, и мы начнем.', '', 'Я ждал встречи с тобой.', 'Варзок', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'zhCN', '瓦佐克', '去奥格瑞玛向瓦佐克报到。', '力量与荣耀，$n。我叫瓦佐克。我近来听说了不少你作为驯宠师所取得的成就，我愿意把你引荐给艾泽拉斯的其他训练师，使你进一步磨练自己的技能。$B$B请到奥格瑞玛来找我聊聊，我们尽快开始。', '', '期待与你会面。', '瓦佐克', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32009, 'zhTW', '伐索克', '到奧格瑪找伐索克。', '勝利，$n。我的名字是伐索克。我最近經常聽說你身為訓練師的成就。我想將你介紹給艾澤拉斯各地的訓練師，這樣你才能更加磨練你的技巧。$B$B作為第一步，請到奧格瑪找我談話。', '', '期待與你見面。', '伐索克', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32010, 'deDE', 'A Celestial Task', 'Earned Revered standing with the Golden Lotus.', 'Are you familiar with the August Celestials? Yu\'lon, Chi-Ji, Xuen, and Niuzao watch over the four corners of Pandaria.$B$BThey watch you as well.$B$BTheir knowledge is most ancient, and most powerful. To those they favor, they can bestow enormous power.$B$BYou may seek to earn their trust, but they need you for a more important task.$B$BThe Vale of Eternal Blossoms is in danger. First, we must all defend the Vale. Then, we can turn our eyes to the Celestials themselves.', '', '', '', '', '', '', 0);
