INSERT INTO `quest_template_locale` VALUES (32105, 'koKR', '온전한 기념물 장부', '유물을 전시해야 합니다.', '유물이 매우 훌륭한 상태로 복원되었습니다. 전승지기와 고고학자들이 연구할 수 있도록 지식의 연단에 전시해도 될 것 같습니다.', '', '', '', '', '', '', 0);
