INSERT INTO `quest_template_locale` VALUES (32107, 'esES', 'Gurami tigre volador', 'Lleva el gurami tigre volador a Nat Pagle al Muelle de los Pescadores.', 'Has pescado un pez único… con alas. Tal vez sea digno de la experta opinión de Nat Pagle en el Muelle de los Pescadores.', '', 'Tal vez Nat sepa algo más acerca de esta rareza.', 'Nat Pagle', '', '', '', 0);
